    ) VALUES (1425014, 'Ibrutinib', 'Lumacaftor', 'Major', 'Coadministration with inducers of CYP450 3A4 may significantly decrease the plasma concentrations of ibrutinib, which is primarily metabolized by the isoenzyme.', NULL, 'The use of ibrutinib in combination with potent CYP450 3A4 inducers such as carbamazepine, enzalutamide, phenobarbital, phenytoin, and rifamycins should generally be avoided.', NULL, 'The use of ibrutinib in combination with potent CYP450 3A4 inducers such as carbamazepine, enzalutamide, phenobarbital, phenytoin, and rifamycins should generally be avoided.', 'Metabolism', NULL, NULL, 'Major', NULL, 'DDInter', 'pharmacodynamic', 1767317476);
