    ) VALUES (1425028, 'Lumacaftor', 'Larotrectinib', 'Major', 'Coadministration with potent inducers of CYP450 3A4 and/or P-glycoprotein (P-gp) may significantly decrease the plasma concentrations of larotrectinib, which is primarily metabolized by the isoenzyme as well as the efflux transporter.', NULL, 'Concomitant use of larotrectinib with potent CYP450 3A4 and/or P-gp inducers should generally be avoided.', NULL, 'Concomitant use of larotrectinib with potent CYP450 3A4 and/or P-gp inducers should generally be avoided. If coadministration is required, the manufacturer recommends doubling the larotrectinib dose. After the inducer has been discontinued for 3 to 5 elimination half-lives, the larotrectinib dose that was taken prior to initiating the inducer may be resumed.', 'Metabolism', NULL, NULL, 'Major', NULL, 'DDInter', 'pharmacodynamic', 1767317476);
