INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, arabic_effect, recommendation, arabic_recommendation, management_text, mechanism_text, alternatives_a, alternatives_b, risk_level, ddinter_id, source, type, metabolism_info, source_url, reference_text, updated_at) VALUES (168928, 'Voriconazole', 'Tazemetostat', 'Major', 'Coadministration with moderate or potent CYP450 3A4 inhibitors may significantly increase the plasma concentrations of tazemetostat, which is primarily metabolized by the isoenzyme. Clinically, this interaction may result in an increased risk of the frequency or severity of adverse reactions due to tazemetostat such as secondary malignancies, hemorrhage, pleural effusion, skin infection, dyspnea, pain, and respiratory distress.', NULL, 'Concomitant use of tazemetostat with moderate or potent CYP450 3A4 inhibitors should generally be avoided.', NULL, 'Concomitant use of tazemetostat with moderate or potent CYP450 3A4 inhibitors should generally be avoided. Alternative therapeutic agents with less inhibitory potential should be considered whenever possible. If coadministration with a moderate CYP450 3A4 inhibitor is required and no other alternatives are possible, the dose of tazemetostat should be reduced as per manufacturer recommendations. After the inhibitor has been discontinued for 3 elimination half-lives, the tazemetostat dose that was taken prior to initiating the inhibitor may be resumed. Patients should be advised to notify their health care professional if they experience signs or symptoms of possible tazemetostat-related adverse effects including secondary malignancies, hemorrhage, pleural effusion, skin infection, dyspnea, pain, and respiratory distress.', 'Metabolism', 'Micafungin, Amphotericin B, Miconazole, Oteseconazole, Flucytosine, Anidulafungin', 'Pertuzumab, Tisagenlecleucel, Omacetaxine mepesuccinate, Altretamine, Tagraxofusp, Ixazomib, Enasidenib, Niraparib, Selinexor, Aflibercept, Trastuzumab, More', 'Major', NULL, 'DDInter', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/169104/', '[1] "Product Information. Tazverik (tazemetostat)." Epizyme, Inc, Cambridge, MA.[2] "Product Information. Tazverik (tazemetostat)." Epizyme, Inc, Cambridge, MA.', 1767369485);
INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, arabic_effect, recommendation, arabic_recommendation, management_text, mechanism_text, alternatives_a, alternatives_b, risk_level, ddinter_id, source, type, metabolism_info, source_url, reference_text, updated_at) VALUES (168929, 'Vortioxetine', 'Tazemetostat', 'Moderate', 'Serotonin reuptake inhibitors (SRIs) may potentiate the risk of bleeding in patients treated with ulcerogenic agents and agents that affect hemostasis such as anticoagulants, platelet inhibitors, thrombin inhibitors, thrombolytic agents, or agents that commonly cause thrombocytopenia. The tricyclic antidepressant, clomipramine, is also a strong SRI and may interact similarly. Serotonin release by platelets plays an important role in hemostasis, thus SRIs may alter platelet function and induce bleeding.', NULL, 'Caution is advised if SRIs or clomipramine are used in combination with other drugs that affect hemostasis.', NULL, 'Caution is advised if SRIs or clomipramine are used in combination with other drugs that affect hemostasis. Close clinical and laboratory observation for hematologic complications is recommended. Patients should be advised to promptly report any signs of bleeding to their physician, including pain, swelling, headache, dizziness, weakness, prolonged bleeding from cuts, increased menstrual flow, vaginal bleeding, nosebleeds, bleeding of gums from brushing, unusual bleeding or bruising, red or brown urine, or red or black stools.', 'Synergism', 'Phenelzine, Desipramine, Tranylcypromine, Trimipramine, Maprotiline, Isocarboxazid, Oxitriptan, Bupropion, Tryptophan, Amitriptyline, Nortriptyline, More', 'Pertuzumab, Olaparib, Oxaliplatin, Rucaparib, Bortezomib, Tisagenlecleucel, Aminolevulinic acid, Omacetaxine mepesuccinate, Altretamine, Tagraxofusp, Pegaspargase, More', 'Moderate', NULL, 'DDInter', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/169105/', '[1] Bannister SJ