INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, source, updated_at, management_text, mechanism_text, recommendation, arabic_recommendation, arabic_effect, risk_level, type) VALUES (1425103, 'Roflumilast', 'Lumacaftor', 'Moderate', 'Coadministration with potent CYP450 inducers may significantly reduce the systemic exposure to roflumilast and its pharmacologically active N-oxide metabolite, the former of which is metabolized by CYP450 1A2 and 3A4, and the latter of which is metabolized by CYP450 2C19 and 3A4.', 'DDInter', 1767317476, 'Due to the potential for reduced therapeutic effectiveness of roflumilast, concomitant use with potent CYP450 enzyme inducers such as carbamazepine, phenobarbital, phenytoin, rifampin, and St. John''s wort is not recommended.', 'Metabolism', 'Due to the potential for reduced therapeutic effectiveness of roflumilast, concomitant use with potent CYP450 enzyme inducers such as carbamazepine, phenobarbital, phenytoin, rifampin, and St.', NULL, NULL, 'Moderate', 'pharmacodynamic');
