INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, source, updated_at, management_text, mechanism_text, recommendation, arabic_recommendation, arabic_effect, risk_level, type) VALUES (1425123, 'Tacrolimus', 'Lumacaftor', 'Major', 'Coadministration with lumacaftor may decrease the plasma concentrations and therapeutic efficacy of drugs that are substrates of CYP450 3A4. Lumacaftor is a potent CYP450 3A4 inducer in vivo. Coadministration of lumacaftor with ivacaftor, a sensitive CYP450 3A4 substrate, decreased ivacaftor systemic exposure (AUC) by approximately 80%.', 'DDInter', 1767317476, 'Concomitant use of lumacaftor is not recommended with sensitive CYP450 3A4 substrates (e.g., oral midazolam, triazolam, lovastatin, simvastatin) or CYP450 3A4 substrates with a narrow therapeutic index (e.g., immunosuppressants such as cyclosporine, everolimus, sirolimus, and tacrolimus). Lumacaftor should also be avoided in patients receiving certain antibiotics (e.g., clarithromycin, erythromycin, telithromycin) and azole antifungal agents (e.g., itraconazole, ketoconazole, posaconazole, voriconazole). Alternatives to these agents should be considered.', 'Metabolism', 'Concomitant use of lumacaftor is not recommended with sensitive CYP450 3A4 substrates (e.', NULL, NULL, 'Major', 'pharmacodynamic');
INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, source, updated_at, management_text, mechanism_text, recommendation, arabic_recommendation, arabic_effect, risk_level, type) VALUES (1425124, 'Tasimelteon', 'Lumacaftor', 'Major', 'Coadministration with potent inducers of CYP450 1A2 and 3A4 may significantly decrease the plasma concentrations of tasimelteon, which is primarily metabolized by these isoenzymes.', 'DDInter', 1767317476, 'Concomitant use of tasimelteon with potent CYP450 3A4 inducers should generally be avoided due to the potential for reduced efficacy.', 'Metabolism', 'Concomitant use of tasimelteon with potent CYP450 3A4 inducers should generally be avoided due to the potential for reduced efficacy.', NULL, NULL, 'Major', 'pharmacodynamic');
INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, source, updated_at, management_text, mechanism_text, recommendation, arabic_recommendation, arabic_effect, risk_level, type) VALUES (1425125, 'Tazemetostat', 'Lumacaftor', 'Major', 'Coadministration with moderate or potent inducers of CYP450 3A4 may significantly decrease the plasma concentrations of tazemetostat, which is primarily metabolized by the isoenzyme.', 'DDInter', 1767317476, 'Concomitant use of tazemetostat with moderate or potent CYP450 3A4 inducers should generally be avoided. Alternative therapeutic agents with less enzyme induction potential should be considered whenever possible.', 'Metabolism', 'Concomitant use of tazemetostat with moderate or potent CYP450 3A4 inducers should generally be avoided.', NULL, NULL, 'Major', 'pharmacodynamic');
INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, source, updated_at, management_text, mechanism_text, recommendation, arabic_recommendation, arabic_effect, risk_level, type) VALUES (1425126, 'Tedizolid', 'Lumacaftor', 'Moderate', 'Coadministration with oral tedizolid may increase the plasma concentrations and the risk of adverse effects of orally administered drugs that are substrates of the breast cancer resistance protein (BCRP) transporter. The proposed mechanism is decreased clearance due to inhibition of BCRP-mediated intestinal efflux by tedizolid.', 'DDInter', 1767317476, 'Consider temporarily interrupting treatment with the BCRP substrate during treatment with tedizolid, especially for BCRP substrates with a narrow therapeutic index. If coadministration cannot be avoided, monitor for adverse reactions related to concomitantly administered BCRP substrate.', 'Absorption', 'Consider temporarily interrupting treatment with the BCRP substrate during treatment with tedizolid, especially for BCRP substrates with a narrow therapeutic index.', NULL, NULL, 'Moderate', 'pharmacodynamic');
INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, source, updated_at, management_text, mechanism_text, recommendation, arabic_recommendation, arabic_effect, risk_level, type) VALUES (1425127, 'Temsirolimus', 'Lumacaftor', 'Major', 'Coadministration of temsirolimus with potent inducers of CYP450 3A4 may significantly decrease the plasma concentrations of sirolimus, a major active metabolite of temsirolimus and known substrate of CYP450 3A4.', 'DDInter', 1767317476, 'Concomitant use of temsirolimus with potent CYP450 3A4 inducers should generally be avoided. If coadministration is required in patients treated with temsirolimus for renal cell carcinoma, the manufacturer recommends increasing the temsirolimus dosage from 25 mg to 50 mg once a week depending on patient tolerability. Based on pharmacokinetic studies, this dosage is predicted to adjust the sirolimus systemic exposure (AUC) to the range observed without inducers. However, clinical data are lacking. The dosage should be reduced to the normally recommended dosage (i.e., 25 mg once a week) following discontinuation of the potent CYP450 3A4 inducer.', 'Metabolism', 'Concomitant use of temsirolimus with potent CYP450 3A4 inducers should generally be avoided.', NULL, NULL, 'Major', 'pharmacodynamic');
INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, source, updated_at, management_text, mechanism_text, recommendation, arabic_recommendation, arabic_effect, risk_level, type) VALUES (1425128, 'Ticagrelor', 'Lumacaftor', 'Major', 'Coadministration with potent inducers of CYP450 3A4 may significantly decrease the plasma concentrations of ticagrelor, which is primarily metabolized by the isoenzyme.', 'DDInter', 1767317476, 'Concomitant use of ticagrelor with potent CYP450 3A4 inducers should generally be avoided.', 'Metabolism', 'Concomitant use of ticagrelor with potent CYP450 3A4 inducers should generally be avoided.', NULL, NULL, 'Major', 'pharmacodynamic');
INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, source, updated_at, management_text, mechanism_text, recommendation, arabic_recommendation, arabic_effect, risk_level, type) VALUES (1425129, 'Tofacitinib', 'Lumacaftor', 'Major', 'Coadministration with potent inducers of CYP450 3A4 may significantly decrease the plasma concentrations of tofacitinib, which is primarily metabolized by the isoenzyme. The interaction may result in loss of or reduced clinical response.', 'DDInter', 1767317476, 'The use of tofacitinib in combination with potent CYP450 3A4 inducers should generally be avoided. Alternative agents with no or minimal CYP450 3A4 induction potential are recommended whenever possible. The extent to which moderate CYP450 3A4 inducers interact with tofacitinib is unknown.', 'Metabolism', 'The use of tofacitinib in combination with potent CYP450 3A4 inducers should generally be avoided.', NULL, NULL, 'Major', 'pharmacodynamic');
INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, source, updated_at, management_text, mechanism_text, recommendation, arabic_recommendation, arabic_effect, risk_level, type) VALUES (1425130, 'Tolvaptan', 'Lumacaftor', 'Major', 'Coadministration with potent inducers of CYP450 3A4 may significantly decrease the plasma concentrations of tolvaptan, which is primarily metabolized by the isoenzyme. When tolvaptan was administered with the potent CYP450 3A4 inducer rifampin, tolvaptan peak plasma concentration (Cmax) and systemic exposure (AUC) decreased by approximately 85%.', 'DDInter', 1767317476, 'Concomitant use of tolvaptan with potent CYP450 3A4 inducers should generally be avoided. If coadministration is necessary, a dosage increase of tolvaptan may be required. Patient response should be closely monitored and the dosage adjusted accordingly.', 'Metabolism', 'Concomitant use of tolvaptan with potent CYP450 3A4 inducers should generally be avoided.', NULL, NULL, 'Major', 'pharmacodynamic');
INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, source, updated_at, management_text, mechanism_text, recommendation, arabic_recommendation, arabic_effect, risk_level, type) VALUES (1425131, 'Toremifene', 'Lumacaftor', 'Major', 'Coadministration with potent inducers of CYP450 3A4 may significantly decrease the plasma concentrations of toremifene.', 'DDInter', 1767317476, 'Concomitant use of toremifene with potent CYP450 3A4 inducers should generally be avoided. Alternative therapeutic agents with less enzyme induction potential should be considered whenever possible during treatment with toremifene. If coadministration is required, a doubling of the toremifene dosage may be necessary depending on patient tolerability. Close monitoring for toxicities (e.g., QT prolongation, hepatotoxicity, hypercalcemia and tumor flare) is recommended if the dosage of toremifene is increased. The dosage should be reduced to the indicated dosage following discontinuation of the potent CYP450 3A4 inducer.', 'Metabolism', 'Concomitant use of toremifene with potent CYP450 3A4 inducers should generally be avoided.', NULL, NULL, 'Major', 'pharmacodynamic');
INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, source, updated_at, management_text, mechanism_text, recommendation, arabic_recommendation, arabic_effect, risk_level, type) VALUES (1425132, 'Trabectedin', 'Lumacaftor', 'Major', 'Coadministration with potent inducers of CYP450 3A4 may decrease the plasma concentrations of trabectedin, which is primarily metabolized by the isoenzyme.', 'DDInter', 1767317476, 'Given the potential for diminished pharmacologic effects of trabectedin in the presence of potent CYP450 3A4 inducers, concomitant use should generally be avoided.', 'Metabolism', 'Given the potential for diminished pharmacologic effects of trabectedin in the presence of potent CYP450 3A4 inducers, concomitant use should generally be avoided.', NULL, NULL, 'Major', 'pharmacodynamic');
