INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, source, updated_at, management_text, mechanism_text, recommendation, arabic_recommendation, arabic_effect, risk_level, type) VALUES (1425137, 'Trimethoprim', 'Lumacaftor', 'Moderate', 'Coadministration with potent inducers of CYP450 2C9 and 3A4 may decrease the plasma concentrations of sulfamethoxazole and trimethoprim. A small amount of sulfamethoxazole is metabolized by CYP450 2C9, while trimethoprim is partially metabolized by both CYP450 2C9 and 3A4.', 'DDInter', 1767317476, 'The potential for diminished pharmacologic effects of sulfamethoxazole-trimethoprim should be considered during coadministration with potent CYP450 2C9 ad 3A4 inducers, particularly when sulfamethoxazole-trimethoprim is used for prevention of toxoplasmic encephalitis in HIV patients because adequate drug levels are required in the central nervous system. It may be advisable to refrain from low-dose prophylactic regimens (less than 4 DS tablets per week). Alternative treatments may be required if an interaction is suspected.', 'Metabolism', 'The potential for diminished pharmacologic effects of sulfamethoxazole-trimethoprim should be considered during coadministration with potent CYP450 2C9 ad 3A4 inducers, particularly when sulfamethoxazole-trimethoprim is used for prevention of toxoplasmic encephalitis in HIV patients because adequate drug levels are required in the central nervous system.', NULL, NULL, 'Moderate', 'pharmacodynamic');
