INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, source, updated_at, management_text, mechanism_text, recommendation, arabic_recommendation, arabic_effect, risk_level, type) VALUES (1425140, 'Valbenazine', 'Lumacaftor', 'Major', 'Coadministration of valbenazine with strong CYP450 3A4 inducers may decrease the plasma concentrations and therapeutic effects of valbenazine and its active metabolite, which are substrates of the isoenzyme. The proposed mechanism is induction of CYP450 3A4-mediated metabolism of valbenazine.', 'DDInter', 1767317476, 'The use of valbenazine with strong CYP450 3A4 inducers should generally be avoided. If concomitant use is required, caution and close clinical and laboratory monitoring are recommended. Dosage adjustments may be required whenever a strong CYP450 3A4 inducer is added to or withdrawn from therapy. Alternative treatment may be considered if an interaction is suspected.', 'Metabolism', 'The use of valbenazine with strong CYP450 3A4 inducers should generally be avoided.', NULL, NULL, 'Major', 'pharmacodynamic');
