INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, source, updated_at, management_text, mechanism_text, recommendation, arabic_recommendation, arabic_effect, risk_level, type) VALUES (1425161, 'Dezocine', 'Pregabalin', 'Major', 'Concomitant use of opioids with gabapentinoids (e.g., gabapentin, pregabalin) may increase the risk of opioid overdose and serious adverse effects such as profound sedation, respiratory depression, syncope, and death due to potentially additive depressant effects on the central nervous system. Coadministration with opioids may increase the oral bioavailability of gabapentin.', 'DDInter', 1767317476, 'Caution is advised when opioids and gabapentinoids are coadministered, particularly in patients with additional risk factors for respiratory depression such as advanced age, renal insufficiency, or chronic lung disease. The dosage and duration of each drug should be limited to the minimum required to achieve desired clinical effect, with cautious titration and dosage adjustments when needed. Use of additional central nervous system depressants should be avoided if possible. Patients should be monitored closely for signs and symptoms of respiratory depression and sedation, and advised to avoid driving or operating hazardous machinery until they know how these medications affect them. For patients who have been receiving extended therapy with both an opioid and a gabapentinoid (either for analgesia or seizure control) and require discontinuation of either medication, a gradual tapering of dose is advised, since abrupt withdrawal may lead to withdrawal symptoms and increased seizure risk.', 'Absorption', 'Caution is advised when opioids and gabapentinoids are coadministered, particularly in patients with additional risk factors for respiratory depression such as advanced age, renal insufficiency, or chronic lung disease.', NULL, NULL, 'Major', 'pharmacodynamic');
