INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, source, updated_at, management_text, mechanism_text, recommendation, arabic_recommendation, arabic_effect, risk_level, type) VALUES (1425181, 'Fosinopril', 'Pregabalin', 'Moderate', 'Pregabalin has been associated with the development of angioedema, and coadministration with other drugs that are also known to cause angioedema (e.g., ACE inhibitors, angiotensin II receptor antagonists, renin inhibitors) may theoretically increase the risk. Specific symptoms included swelling of the face, mouth (tongue, lips, and gums), and neck (throat and larynx). In some cases, the angioedema led to life-threatening respiratory compromise that required emergency treatment.', 'DDInter', 1767317476, 'Clinicians and patients should be aware of the potential for increased risk of angioedema when pregabalin is prescribed with other drugs that are also known to cause angioedema. Patients should be advised to promptly discontinue these medications and seek medical attention if they develop signs or symptoms suggestive of angioedema such as swelling of the face, extremities, eyes, lips, or tongue, and difficulty swallowing or breathing.', 'Synergism', 'Clinicians and patients should be aware of the potential for increased risk of angioedema when pregabalin is prescribed with other drugs that are also known to cause angioedema.', NULL, NULL, 'Moderate', 'pharmacodynamic');
