INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, source, updated_at, management_text, mechanism_text, recommendation, arabic_recommendation, arabic_effect, risk_level, type) VALUES (1425204, 'Pregabalin', 'Lisinopril', 'Moderate', 'Pregabalin has been associated with the development of angioedema, and coadministration with other drugs that are also known to cause angioedema (e.g., ACE inhibitors, angiotensin II receptor antagonists, renin inhibitors) may theoretically increase the risk. Specific symptoms included swelling of the face, mouth (tongue, lips, and gums), and neck (throat and larynx). In some cases, the angioedema led to life-threatening respiratory compromise that required emergency treatment.', 'DDInter', 1767317476, 'Clinicians and patients should be aware of the potential for increased risk of angioedema when pregabalin is prescribed with other drugs that are also known to cause angioedema. Patients should be advised to promptly discontinue these medications and seek medical attention if they develop signs or symptoms suggestive of angioedema such as swelling of the face, extremities, eyes, lips, or tongue, and difficulty swallowing or breathing.', 'Synergism', 'Clinicians and patients should be aware of the potential for increased risk of angioedema when pregabalin is prescribed with other drugs that are also known to cause angioedema.', NULL, NULL, 'Moderate', 'pharmacodynamic');
INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, source, updated_at, management_text, mechanism_text, recommendation, arabic_recommendation, arabic_effect, risk_level, type) VALUES (1425205, 'Pregabalin', 'Lithium carbonate', 'Moderate', 'Central nervous system- and/or respiratory-depressant effects may be additively or synergistically increased in patients taking multiple drugs that cause these effects, especially in elderly or debilitated patients.', 'DDInter', 1767317476, 'During concomitant use of these drugs, patients should be monitored for potentially excessive or prolonged CNS and respiratory depression. Cautious dosage titration may be required, particularly at treatment initiation. Ambulatory patients should be counseled to avoid hazardous activities requiring mental alertness and motor coordination.', 'Synergism', 'During concomitant use of these drugs, patients should be monitored for potentially excessive or prolonged CNS and respiratory depression.', NULL, NULL, 'Moderate', 'pharmacodynamic');
