INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, source, updated_at, management_text, mechanism_text, recommendation, arabic_recommendation, arabic_effect, risk_level, type) VALUES (1425280, 'Rosiglitazone', 'Pregabalin', 'Moderate', 'Higher frequencies of weight gain and peripheral edema were observed in patients taking both pregabalin and a thiazolidinedione antidiabetic agent compared to patients taking either drug alone, presumably due to additive effects during coadministration.', 'DDInter', 1767317476, 'Since fluid retention and weight gain may exacerbate or lead to heart failure in susceptible patients, caution is advised if pregabalin is used in combination with a thiazolidinedione. Because there are limited data on congestive heart failure patients with New York Heart Association (NYHA) Class III or IV cardiac status, pregabalin with or without a thiazolidinedione should be used cautiously in such patients.', 'Synergism', 'Since fluid retention and weight gain may exacerbate or lead to heart failure in susceptible patients, caution is advised if pregabalin is used in combination with a thiazolidinedione.', NULL, NULL, 'Moderate', 'pharmacodynamic');
