INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, source, updated_at, management_text, mechanism_text, recommendation, arabic_recommendation, arabic_effect, risk_level, type) VALUES (1425354, 'Tretinoin', 'Tranexamic acid', 'Major', 'Coadministration of tretinoin (all-trans retinoic acid) with antifibrinolytic agents may increase the risk of thrombosis. Tretinoin is associated with a risk of both venous and arterial thrombosis during the first month of treatment that may involve any organ system. Cases of fatal thrombotic complications have been reported rarely in patients concomitantly treated with tretinoin and antifibrinolytic agents.', 'DDInter', 1767317476, 'Caution is advised when tretinoin is prescribed in combination with antifibrinolytic agents such as aminocaproic acid, aprotinin, or tranexamic acid. Patients should be advised to seek medical attention immediately if they experience potential signs and symptoms of blood clots such as chest pain, shortness of breath, hemoptysis, hematuria, sudden loss of vision, and pain, redness or swelling in an extremity.', 'Synergism', 'Caution is advised when tretinoin is prescribed in combination with antifibrinolytic agents such as aminocaproic acid, aprotinin, or tranexamic acid.', NULL, NULL, 'Major', 'pharmacodynamic');
