INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, arabic_effect, recommendation, arabic_recommendation, management_text, mechanism_text, alternatives_a, alternatives_b, risk_level, ddinter_id, source, type, metabolism_info, source_url, reference_text, updated_at) VALUES (169158, 'Fesoterodine', 'Telithromycin', 'Major', 'Coadministration with potent inhibitors of CYP450 3A4 may increase the plasma concentrations of fesoterodine''s active metabolite, 5-hydroxymethyl tolterodine, which is partially metabolized by the isoenzyme. The possibility of prolonged and/or increased pharmacologic effects of fesoterodine should be considered. Because 5-hydroxymethyl tolterodine is also metabolized by CYP450 2D6, the clinical significance of the interaction may be greater in patients who are CYP450 2D6-deficient, or so-called poor metabolizers of CYP450 2D6 who may rely more on the 3A4 metabolic pathway for clearance of the drug.', NULL, 'The dosage of fesoterodine should not exceed 4 mg/day when used with potent CYP450 3A4 inhibitors.', NULL, 'The dosage of fesoterodine should not exceed 4 mg/day when used with potent CYP450 3A4 inhibitors. Close clinical and laboratory monitoring is advised whenever a CYP450 3A4 inhibitor is added to or withdrawn from therapy. Patients should be advised to contact their physician if they experience potential adverse effects of fesoterodine such as irregular heartbeat, blurry vision, difficulty urinating, dry mouth, headache, drowsiness, dizziness, gastrointestinal upset, or constipation. The product labeling for itraconazole (a potent CYP450 3A4 inhibitor) states that concomitant use with fesoterodine is contraindicated in patients with moderate to severe renal or hepatic impairment during and for 2 weeks after treatment with itraconazole.', 'Metabolism', NULL, NULL, 'Major', NULL, 'DDInter', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/169334/', '[1] "Product Information. Toviaz (fesoterodine)." Pfizer U.S. Pharmaceuticals Group, New York, NY.[2] "Product Information. Sporanox (itraconazole)." Janssen Pharmaceutica, Titusville, NJ.[3] Cerner Multum, Inc. "Australian Product Information." O 0[4] "Product Information. Sporonox (itraconazole)." Janssen Pharmaceutica, Titusville, NJ.[5] "Product Information. Toviaz (fesoterodine)." Pfizer U.S. Pharmaceuticals Group, New York, NY.', 1767363026);
INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, arabic_effect, recommendation, arabic_recommendation, management_text, mechanism_text, alternatives_a, alternatives_b, risk_level, ddinter_id, source, type, metabolism_info, source_url, reference_text, updated_at) VALUES (169159, 'Fidaxomicin', 'Telithromycin', 'Minor', 'Coadministration with inhibitors of P-glycoprotein (P-gp) may increase the plasma concentrations of fidaxomicin and its main metabolite, OP-1118, both of which are substrates of the intestinal efflux transporter. Theoretically, concentrations of fidaxomicin and OP-1118 may also be decreased at the site of action (i.e., gastrointestinal tract) due to P-gp inhibition.', NULL, '-', NULL, '-', 'Absorption', NULL, NULL, 'Minor', NULL, 'DDInter', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/169335/', '[1] Cerner Multum, Inc. "UK Summary of Product Characteristics." O 0[2] Cerner Multum, Inc. "Australian Product Information." O 0[3] "Product Information. Dificid (fidaxomicin)." Optimer Pharmaceuticals, San Diego, CA.[4] Cerner Multum, Inc. "UK Summary of Product Characteristics." O 0[5] Cerner Multum, Inc. "Australian Product Information." O 0[6] "Product Information. Dificid (fidaxomicin)." Optimer Pharmaceuticals, San Diego, CA.[7] "Product Information. Mavyret (glecaprevir-pibrentasvir)." Abbott Pharmaceutical, Abbott Park, IL.', 1767363026);
INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, arabic_effect, recommendation, arabic_recommendation, management_text, mechanism_text, alternatives_a, alternatives_b, risk_level, ddinter_id, source, type, metabolism_info, source_url, reference_text, updated_at) VALUES (169160, 'Fingolimod', 'Telithromycin', 'Major', 'Due to its significant bradycardic effects, the risk of QT prolongation and torsade de pointes arrhythmia may be increased during initiation of fingolimod treatment in patients receiving drugs that prolong the QT interval. Fingolimod can cause a decrease in heart rate during initiation of therapy that is apparent within an hour of the first dose and maximal at approximately 6 hours postdose in most cases, but occasionally up to 20 hours after the first dose.', NULL, 'Fingolimod has not been studied in patients receiving drugs that can prolong the QT interval.', NULL, 'Fingolimod has not been studied in patients receiving drugs that can prolong the QT interval. Because bradycardia and AV block are recognized risk factors for QT prolongation and torsade de pointes arrhythmia, close monitoring is recommended during initiation of fingolimod treatment in patients receiving concomitant drugs that can prolong the QT interval, patients with significant QT prolongation (QTc >470 msec in females or >450 msec in males), or patients with relevant risk factors for QT prolongation (e.g., hypokalemia, hypomagnesemia, congenital QT prolongation).', 'Synergism', NULL, NULL, 'Major', NULL, 'DDInter', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/169336/', '[1] Cerner Multum, Inc. "Australian Product Information." O 0[2] Canadian Pharmacists Association "e-CPS. Available from: URL: http://www.pharmacists.ca/function/Subscriptions/ecps.cfm?link=eCPS_quikLink."[3] "Product Information. Gilenya (fingolimod)." Novartis Pharmaceuticals, East Hanover, NJ.[4] FDA. U.S. Food and Drug Administration "FDA Drug Safety Communication: Revised recommendations for cardiovascular monitoring and use of multiple sclerosis drug Gilenya (fingolimod). Available from: URL: http://www.fda.gov/Drugs/DrugSafety/ucm303192.htm#data." ([2012 May 14]):[5] Cerner Multum, Inc. "UK Summary of Product Characteristics." O 0', 1767363026);
