INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, arabic_effect, recommendation, arabic_recommendation, management_text, mechanism_text, alternatives_a, alternatives_b, risk_level, ddinter_id, source, type, metabolism_info, source_url, reference_text, updated_at) VALUES (169174, 'Fosphenytoin', 'Telithromycin', 'Major', 'Coadministration with potent inducers of CYP450 3A4 may significantly decrease the plasma concentrations of telithromycin, which is primarily metabolized by the isoenzyme. Enzyme activities usually return to normal 14 days after discontinuation of the inducing agent.', NULL, 'Telithromycin should not be used during or within 2 weeks after discontinuation of treatment with a potent CYP450 3A4 inducer.', NULL, 'Telithromycin should not be used during or within 2 weeks after discontinuation of treatment with a potent CYP450 3A4 inducer.', 'Metabolism', NULL, NULL, 'Major', NULL, 'DDInter', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/169350/', '[1] "Product Information. Ketek (telithromycin)." Aventis Pharmaceuticals, Bridgewater, NJ.[2] Cerner Multum, Inc. "UK Summary of Product Characteristics." O 0[3] European Agency for the Evaluation of Medicinal Products. Committee for Proprietary Medicinal Products "European Public Assessment Report Ketek (telithromycin) (Rev. 2) Available from: URL: http:www.emea.eu.int/humandocs/Humans/EPAR/Ketek/Ketek.htm." ([2001]):[4] Patel S, Robinson R, Burk M "Hypertensive crisis associated with St. John''s Wort." Am J Med 112 (2002): 507-8', 1767363026);
INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, arabic_effect, recommendation, arabic_recommendation, management_text, mechanism_text, alternatives_a, alternatives_b, risk_level, ddinter_id, source, type, metabolism_info, source_url, reference_text, updated_at) VALUES (169175, 'Fostamatinib', 'Telithromycin', 'Major', 'Coadministration of fostamatinib with potent inhibitors of CYP450 3A4 may significantly increase exposure to the active metabolite known as R406, the predominant moiety in the systemic circulation following fostamatinib administration. Fostamatinib is metabolized in the gut by alkaline phosphatase to R406, which then undergoes oxidation via CYP450 3A4 and glucuronidation via UGT1A9. Increased exposure to R406 may result in increased risk of adverse effects such as diarrhea, hepatotoxicity, hypertension, and neutropenia.', NULL, 'Patients should be monitored for toxicities of fostamatinib during concomitant use of potent CYP450 3A4 inhibitors, and the fostamatinib dosage adjusted as necessary in accordance with the product labeling.', NULL, 'Patients should be monitored for toxicities of fostamatinib during concomitant use of potent CYP450 3A4 inhibitors, and the fostamatinib dosage adjusted as necessary in accordance with the product labeling.', 'Metabolism', NULL, NULL, 'Major', NULL, 'DDInter', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/169351/', '[1] "Product Information. Tavalisse (fostamatinib)." Rigel Pharmaceuticals, South San Francisco, CA.', 1767363026);
INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, arabic_effect, recommendation, arabic_recommendation, management_text, mechanism_text, alternatives_a, alternatives_b, risk_level, ddinter_id, source, type, metabolism_info, source_url, reference_text, updated_at) VALUES (169176, 'Fostemsavir', 'Telithromycin', 'Moderate', 'Fostemsavir can cause dose-related prolongation of the QT interval. Theoretically, coadministration with other agents that can prolong the QT interval may result in additive effects and increased risk of ventricular arrhythmias including torsade de pointes and sudden death.', NULL, 'Caution is recommended if fostemsavir is used in combination with other drugs that can prolong the QT interval.', NULL, 'Caution is recommended if fostemsavir is used in combination with other drugs that can prolong the QT interval. Consider obtaining electrocardiogram and serum electrolyte levels prior to initiating fostemsavir therapy and periodically thereafter, particularly in patients with risk factors for torsade de pointes and/or receiving one or more potent CYP450 3A4 inhibitors (e.g., azole antifungal agents; cobicistat; protease inhibitors; ketolide and certain macrolide antibiotics; ceritinib; idelalisib; nefazodone) or P-gp inhibitors (e.g., cyclosporine; diltiazem; quinidine; quinine; tacrolimus; verapamil). Correct hypokalemia and/or hypomagnesemia before starting treatment and as indicated during treatment, as they may be risk factors for ventricular arrhythmias.', 'Synergism', NULL, NULL, 'Moderate', NULL, 'DDInter', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/169352/', '[1] "Product Information. Rukobia (fostemsavir)." ViiV Healthcare, Research Triangle Park, NC.', 1767363026);
INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, arabic_effect, recommendation, arabic_recommendation, management_text, mechanism_text, alternatives_a, alternatives_b, risk_level, ddinter_id, source, type, metabolism_info, source_url, reference_text, updated_at) VALUES (169177, 'Galantamine', 'Telithromycin', 'Moderate', 'Coadministration with potent inhibitors of CYP450 3A4 may increase the plasma concentrations of galantamine, which is partially metabolized by the isoenzyme.', NULL, 'Pharmacologic response to galantamine should be monitored more closely whenever a potent CYP450 3A4 inhibitor (e.', NULL, 'Pharmacologic response to galantamine should be monitored more closely whenever a potent CYP450 3A4 inhibitor (e.g., clarithromycin, telithromycin, itraconazole, ketoconazole, posaconazole, voriconazole, conivaptan, nefazodone, cobicistat, delavirdine, protease inhibitors) is added to or withdrawn from therapy, and the galantamine dosage adjusted as necessary. Patients should be advised to notify their physician if they experience excessive cholinergic symptoms such as severe nausea, vomiting, GI cramping, salivation, lacrimation, sweating, dizziness, or syncope.', 'Metabolism', NULL, NULL, 'Moderate', NULL, 'DDInter', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/169353/', '[1] "Product Information. Reminyl (galantamine)" Janssen Pharmaceuticals, Titusville, NJ.', 1767363026);
