INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, arabic_effect, recommendation, arabic_recommendation, management_text, mechanism_text, alternatives_a, alternatives_b, risk_level, ddinter_id, source, type, metabolism_info, source_url, reference_text, updated_at) VALUES (169207, 'Telithromycin', 'Ketoconazole', 'Major', 'Coadministration with itraconazole or ketoconazole may increase the plasma concentrations of telithromycin. The proposed mechanism is inhibition of telithromycin metabolism via CYP450 3A4. Elevated plasma levels of telithromycin may increase the risk of QT interval prolongation, which has been associated with ventricular arrhythmias including torsade de pointes and sudden death. Azole antifungal agents can also prolong the QT interval, thus an additive pharmacodynamic effect with telithromycin may occur as well.', NULL, 'Caution is advised if telithromycin is prescribed with itraconazole or ketoconazole.', NULL, 'Caution is advised if telithromycin is prescribed with itraconazole or ketoconazole. Patients should be advised to seek prompt medical attention if they experience symptoms that could indicate the occurrence of torsade de pointes such as dizziness, lightheadedness, fainting, palpitation, irregular heart rhythm, shortness of breath, or syncope. The product labeling for itraconazole states that concomitant use with telithromycin is contraindicated in patients with severe renal or hepatic impairment.', 'Metabolism', NULL, NULL, 'Major', NULL, 'DDInter', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/169383/', '[1] "Product Information. Ketek (telithromycin)." Aventis Pharmaceuticals, Bridgewater, NJ.[2] "Product Information. Sporonox (itraconazole)." Janssen Pharmaceutica, Titusville, NJ.[3] European Agency for the Evaluation of Medicinal Products. Committee for Proprietary Medicinal Products "European Public Assessment Report Ketek (telithromycin) (Rev. 2) Available from: URL: http:www.emea.eu.int/humandocs/Humans/EPAR/Ketek/Ketek.htm." ([2001]):', 1767363026);
