INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, source, updated_at, management_text, mechanism_text, recommendation, arabic_recommendation, arabic_effect, risk_level, type) VALUES (1425598, 'Famciclovir', 'Varicella Zoster Vaccine (Recombinant)', 'Major', 'Concomitant use of live, attenuated varicella virus vaccine or zoster vaccine with acyclovir, famciclovir, or valacyclovir may result in diminished immunologic response to the vaccine. Although clinical data are lacking, theoretical concern exists that antiviral activity against the vaccine strain may prevent a sufficient degree of replication to occur in order to induce a protective immune response. Acyclovir, specifically, has demonstrated activity against the Oka strain of live, attenuated varicella-zoster virus used in the vaccines.', 'DDInter', 1767317476, 'Acyclovir, famciclovir, and valacyclovir should not be used for at least 24 hours before administration of live, attenuated varicella virus vaccine or zoster vaccine. In addition, these antiviral agents should generally be avoided for 14 days after vaccination.', 'Antagonism', 'Acyclovir, famciclovir, and valacyclovir should not be used for at least 24 hours before administration of live, attenuated varicella virus vaccine or zoster vaccine.', NULL, NULL, 'Major', 'pharmacodynamic');
INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, source, updated_at, management_text, mechanism_text, recommendation, arabic_recommendation, arabic_effect, risk_level, type) VALUES (1425599, 'Fingolimod', 'Varicella Zoster Vaccine (Recombinant)', 'Major', 'The administration of live, attenuated viral or bacterial vaccines during fingolimod therapy may be associated with a risk of disseminated infection due to enhanced replication of vaccine virus or bacteria in the presence of diminished immune competence. Fingolimod causes reversible sequestration of lymphocytes in lymphoid tissues.', 'DDInter', 1767317476, 'The use of live attenuated vaccines should be avoided during and for two months after treatment with fingolimod. In patients who have recently been vaccinated, fingolimod therapy should be postponed for one month to allow the full effect of vaccination to occur.', 'Others', 'The use of live attenuated vaccines should be avoided during and for two months after treatment with fingolimod.', NULL, NULL, 'Major', 'pharmacodynamic');
