INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, source, updated_at, management_text, mechanism_text, recommendation, arabic_recommendation, arabic_effect, risk_level, type) VALUES (1425654, 'Ocrelizumab', 'Measles virus vaccine live attenuated', 'Major', 'The administration of live, attenuated virus or bacterial vaccines during immunosuppressant therapy may be associated with a risk of disseminated infection due to enhanced replication of vaccine virus or bacteria in the presence of diminished immune competence. Additionally, there is a lack of data on the safety of immunization following discontinuation of ocrelizumab. Ocrelizumab use causes a depletion of B-cells and can be expected to similarly increase the risk of a decreased or suboptimal immunologic vaccine response if immunization occurs before B-cells counts have recovered.', 'DDInter', 1767317476, 'Live virus or bacterial vaccines should not be administered to patients during therapy or after discontinuing ocrelizumab therapy until recovery from B-cell repletion occurs. Immunization status should be assessed prior to initiating ocrelizumab and recommended immunization with live or attenuated live vaccines should be completed at least 4 weeks prior to ocrelizumab initiation (some authorities have recommended at least 6 weeks). For infants of mothers exposed to ocrelizumab during pregnancy, live or live-attenuated vaccines should be delayed until B-cell levels have recovered; therefore, measuring CD19 positive B-cell levels prior to vaccination in these infants is recommended.', 'Others', 'Live virus or bacterial vaccines should not be administered to patients during therapy or after discontinuing ocrelizumab therapy until recovery from B-cell repletion occurs.', NULL, NULL, 'Major', 'pharmacodynamic');
