INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, source, updated_at, management_text, mechanism_text, recommendation, arabic_recommendation, arabic_effect, risk_level, type) VALUES (1425693, 'Siponimod', 'Measles virus vaccine live attenuated', 'Major', 'The administration of live, attenuated viral or bacterial vaccines during siponimod therapy may be associated with a risk of disseminated infection due to enhanced replication of vaccine virus or bacteria in the presence of diminished immune competence. Siponimod causes reversible sequestration of lymphocytes in lymphoid tissues.', 'DDInter', 1767317476, 'The use of live attenuated vaccines should be avoided during and for up to 4 weeks after siponimod treatment, and siponimod should be paused 1 week prior to and until 4 weeks after a planned vaccination. Patients without a healthcare professional confirmed history of varicella or without documentation of a full course of vaccination against varicella-zoster virus (VZV) should be tested for antibodies to VZV before initiating treatment with siponimod. For antibody-negative patients, a full course of vaccination with varicella vaccine is recommended prior to treatment with siponimod. In patients who have recently been vaccinated, siponimod therapy should be postponed for 4 weeks to allow the full effect of vaccination to occur.', 'Others', 'The use of live attenuated vaccines should be avoided during and for up to 4 weeks after siponimod treatment, and siponimod should be paused 1 week prior to and until 4 weeks after a planned vaccination.', NULL, NULL, 'Major', 'pharmacodynamic');
