INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, arabic_effect, recommendation, arabic_recommendation, management_text, mechanism_text, alternatives_a, alternatives_b, risk_level, ddinter_id, source, type, metabolism_info, source_url, reference_text, updated_at) VALUES (169687, 'Entecavir', 'Temsirolimus', 'Moderate', 'Because entecavir is primarily eliminated by renal excretion, coadministration with drugs that affect renal function may alter the plasma concentrations of entecavir and/or the coadministered drug. In patients with renal impairment, the apparent oral clearance of entecavir has been shown to decrease as the creatinine clearance decreases.', NULL, 'Caution is advised if entecavir is prescribed in combination with potentially nephrotoxic drugs (e.', NULL, 'Caution is advised if entecavir is prescribed in combination with potentially nephrotoxic drugs (e.g., aminoglycosides; polypeptide, glycopeptide, and polymyxin antibiotics; amphotericin B; adefovir; cidofovir; tenofovir; foscarnet; cisplatin; deferasirox; gallium nitrate; lithium; mesalamine; certain immunosuppressants; intravenous bisphosphonates; intravenous pentamidine; high intravenous dosages of methotrexate; high dosages and/or chronic use of nonsteroidal anti-inflammatory agents). Renal function should be evaluated prior to and during therapy with entecavir.', 'Excretion', NULL, NULL, 'Moderate', NULL, 'DDInter', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/169863/', '[1] "Product Information. Baraclude (entecavir)." Bristol-Myers Squibb, Princeton, NJ.[2] "Product Information. Baraclude (entecavir)." Bristol-Myers Squibb, Princeton, NJ.', 1767363026);
INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, arabic_effect, recommendation, arabic_recommendation, management_text, mechanism_text, alternatives_a, alternatives_b, risk_level, ddinter_id, source, type, metabolism_info, source_url, reference_text, updated_at) VALUES (169688, 'Entrectinib', 'Temsirolimus', 'Moderate', 'Coadministration with entrectinib may increase the plasma concentrations of drugs that are primarily metabolized by the CYP450 3A4 isoenzyme. The mechanism is reduced clearance due to inhibition of CYP450 3A4 by entrectinib. The interaction may be particularly important for sensitive CYP450 3A4 substrates or those that demonstrate a narrow therapeutic index.', NULL, 'Caution may be advisable when entrectinib is used with sensitive CYP450 3A4 substrates or those that demonstrate a narrow therapeutic index (e.', NULL, 'Caution may be advisable when entrectinib is used with sensitive CYP450 3A4 substrates or those that demonstrate a narrow therapeutic index (e.g., ergot alkaloids, colchicine, fentanyl, lovastatin, macrolide immunosuppressants, midazolam, sildenafil, simvastatin, triazolam, vardenafil, vinca alkaloids). Dosage adjustments as well as clinical and laboratory monitoring may be appropriate whenever entrectinib is added to or withdrawn from therapy.', 'Metabolism', NULL, NULL, 'Moderate', NULL, 'DDInter', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/169864/', '[1] "Product Information. Rozlytrek (entrectinib)." Genentech, South San Francisco, CA.[2] Cerner Multum, Inc. "UK Summary of Product Characteristics." O 0[3] "Product Information. Rozlytrek (entrectinib)." Genentech, South San Francisco, CA.', 1767363026);
INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, arabic_effect, recommendation, arabic_recommendation, management_text, mechanism_text, alternatives_a, alternatives_b, risk_level, ddinter_id, source, type, metabolism_info, source_url, reference_text, updated_at) VALUES (169689, 'Enzalutamide', 'Temsirolimus', 'Major', 'Coadministration of temsirolimus with potent inducers of CYP450 3A4 may significantly decrease the plasma concentrations of sirolimus, a major active metabolite of temsirolimus and known substrate of CYP450 3A4.', NULL, 'Concomitant use of temsirolimus with potent CYP450 3A4 inducers should generally be avoided.', NULL, 'Concomitant use of temsirolimus with potent CYP450 3A4 inducers should generally be avoided. If coadministration is required in patients treated with temsirolimus for renal cell carcinoma, the manufacturer recommends increasing the temsirolimus dosage from 25 mg to 50 mg once a week depending on patient tolerability. Based on pharmacokinetic studies, this dosage is predicted to adjust the sirolimus systemic exposure (AUC) to the range observed without inducers. However, clinical data are lacking. The dosage should be reduced to the normally recommended dosage (i.e., 25 mg once a week) following discontinuation of the potent CYP450 3A4 inducer.', 'Metabolism', NULL, NULL, 'Major', NULL, 'DDInter', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/169865/', '[1] "Product Information. Torisel (temsirolimus)." Wyeth-Ayerst Laboratories, Philadelphia, PA.[2] Cerner Multum, Inc. "Australian Product Information." O 0[3] Cerner Multum, Inc. "UK Summary of Product Characteristics." O 0[4] "Product Information. Torisel (temsirolimus)." Wyeth-Ayerst Laboratories, Philadelphia, PA.', 1767363026);
