INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, source, updated_at, management_text, mechanism_text, recommendation, arabic_recommendation, arabic_effect, risk_level, type) VALUES (1425716, 'Triamcinolone (ophthalmic)', 'Mumps virus strain B level jeryl lynn live antigen', 'Moderate', 'The administration of live, attenuated virus or bacterial vaccines during corticosteroid therapy may be associated with a risk of disseminated infection due to enhanced replication of vaccine virus or bacteria in the presence of diminished immune competence.', 'DDInter', 1767317476, 'In general, live attenuated vaccines should not be used in patients receiving immunosuppressive corticosteroid therapy. Vaccination should be deferred until after such therapy is discontinued and immune function has been restored. The interval depends on the dosage and duration of corticosteroid therapy administered, but may be at least 3 months in most cases. Current local immunization guidelines should be consulted for recommendations. In patients who have recently been vaccinated, high-dose corticosteroid therapy should not be initiated for at least 2 weeks. Vaccines may generally be administered to patients receiving corticosteroids as replacement therapy (e.g., for Addison''s disease).', 'Others', 'In general, live attenuated vaccines should not be used in patients receiving immunosuppressive corticosteroid therapy.', NULL, NULL, 'Moderate', 'pharmacodynamic');
INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, source, updated_at, management_text, mechanism_text, recommendation, arabic_recommendation, arabic_effect, risk_level, type) VALUES (1425717, 'Trichophyton mentagrophytes', 'Measles virus vaccine live attenuated', 'Moderate', 'INTERVAL: Reactivity to diagnostic skin test antigens may be temporarily depressed in patients who have recently been vaccinated with live virus vaccines. Falsely insignificant or false-negative results may occur in such patients.', 'DDInter', 1767317476, 'Clinicians should be aware of the potential for falsely insignificant or false-negative results when performing delayed-hypersensitivity skin testing on patients who have recently received live virus vaccines. Diagnostic skin test antigens should be administered before, simultaneously with, or 4 to 6 weeks after immunization.', 'Others', 'Clinicians should be aware of the potential for falsely insignificant or false-negative results when performing delayed-hypersensitivity skin testing on patients who have recently received live virus vaccines.', NULL, NULL, 'Moderate', 'pharmacodynamic');
INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, source, updated_at, management_text, mechanism_text, recommendation, arabic_recommendation, arabic_effect, risk_level, type) VALUES (1425718, 'Trimetrexate', 'Rubella virus vaccine', 'Major', 'The administration of live, attenuated viral or bacterial vaccines during immunosuppressant or intense antineoplastic therapy may be associated with a risk of disseminated infection due to enhanced replication of vaccine virus or bacteria in the presence of diminished immune competence. Patients may be immunosuppressed if they have recently received or are receiving alkylating agents, antimetabolites, radiation, some antirheumatic agents, high dosages of corticosteroids or adrenocorticotropic agents, or long-term topical or inhaled corticosteroids. These patients may also have increased adverse reactions and decreased or suboptimal immunologic response to vaccines.', 'DDInter', 1767317476, 'In general, live attenuated vaccines should not be used in patients receiving immunosuppressive therapy or cancer chemotherapy. Vaccination should be deferred until after such therapy is discontinued and immune function has been restored, usually 4 to 12 weeks after stopping immunosuppressive therapy. A longer waiting period may be necessary following treatment with agents that have a prolonged elimination half-life (e.g., leflunomide, teriflunomide). Current local immunization guidelines should be consulted for recommendations.', 'Others', 'In general, live attenuated vaccines should not be used in patients receiving immunosuppressive therapy or cancer chemotherapy.', NULL, NULL, 'Major', 'pharmacodynamic');
