INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, source, updated_at, management_text, mechanism_text, recommendation, arabic_recommendation, arabic_effect, risk_level, type) VALUES (1425802, 'Trimethoprim', 'Memantine', 'Moderate', 'Trimethoprim may decrease the renal clearance of amantadine and memantine. The mechanism may be competition for organic cation transporter-2 (OCT-2)-mediated secretion in the kidney tubule of which amantadine, memantine, and trimethoprim are substrates. Trimethoprim may also inhibit OCT-2. Increased levels of amantadine and memantine may increase the risk of neurological side effects such as mental confusion, delirium, and myoclonus. Serum trimethoprim levels may also be increased.', 'DDInter', 1767317476, 'Caution and monitoring for nervous system side effects is recommended if trimethoprim is used with either amantadine or memantine. Patients should be advised to contact their physician if they experience nervous system side effects such as mental confusion and myoclonus.', 'Excretion', 'Caution and monitoring for nervous system side effects is recommended if trimethoprim is used with either amantadine or memantine.', NULL, NULL, 'Moderate', 'pharmacodynamic');
