INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, arabic_effect, recommendation, arabic_recommendation, management_text, mechanism_text, alternatives_a, alternatives_b, risk_level, ddinter_id, source, type, metabolism_info, source_url, reference_text, updated_at) VALUES (170572, 'Thalidomide', 'Kava', 'Moderate', 'Thalidomide can commonly cause drowsiness/sedation and may have additive effects with other drugs that cause central nervous system (CNS) depression.', NULL, 'Caution is advised when thalidomide is used with other CNS depressants such as anticonvulsants, antihistamines, antidepressants, antipsychotics, anxiolytics, opioids, and sedatives/hypnotics.', NULL, 'Caution is advised when thalidomide is used with other CNS depressants such as anticonvulsants, antihistamines, antidepressants, antipsychotics, anxiolytics, opioids, and sedatives/hypnotics. Patients should be advised to avoid hazardous activities requiring mental alertness and motor coordination until they know how these agents affect them, and to notify their physician if they experience excessive or prolonged CNS effects that interfere with their normal activities. Dosage reductions may be necessary. Thalidomide should preferably be administered at bedtime to reduce the impact of somnolence. Alcohol use is not recommended.', 'Synergism', NULL, NULL, 'Moderate', NULL, 'DDInter', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/170758/', '[1] "Product Information. Thalomid (thalidomide)." Celgene Corporation, Warren, NJ.[2] Warrington SJ, Ankier SI, Turner P "Evaluation of possible interactions between ethanol and trazodone or amitriptyline." Neuropsychobiology 15 (1986): 31-7[3] "Product Information. Fycompa (perampanel)." Eisai Inc, Teaneck, NJ.[4] Gilman AG, Rall TW, Nies AS, Taylor P, eds. "Goodman and Gilman''s the Pharmacological Basis of Therapeutics. 8th ed." New York, NY: Pergamon Press Inc. (1990):[5] "Product Information. Rexulti (brexpiprazole)." Otsuka American Pharmaceuticals Inc, Rockville, MD.[6] Warrington SJ, Ankier SI, Turner P "Evaluation of possible interactions between ethanol and trazodone or amitriptyline." Neuropsychobiology 15 (1986): 31-7[7] "Product Information. Fycompa (perampanel)." Eisai Inc, Teaneck, NJ.[8] Gilman AG, Rall TW, Nies AS, Taylor P, eds. "Goodman and Gilman''s the Pharmacological Basis of Therapeutics. 8th ed." New York, NY: Pergamon Press Inc. (1990):[9] "Product Information. Rexulti (brexpiprazole)." Otsuka American Pharmaceuticals Inc, Rockville, MD.', 1767365209);
INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, arabic_effect, recommendation, arabic_recommendation, management_text, mechanism_text, alternatives_a, alternatives_b, risk_level, ddinter_id, source, type, metabolism_info, source_url, reference_text, updated_at) VALUES (170573, 'Thalidomide', 'Ketoconazole', 'Moderate', 'Thalidomide can cause bradycardia and may have additive effects with other drugs that decrease heart rate. Cases of bradycardia have been associated with thalidomide use, some requiring medical interventions. The clinical significance and underlying etiology of the bradycardia observed with thalidomide treatment have not been established. However, the potential for additive bradycardic effects should be considered when used with other drugs that slow cardiac conduction such as beta-blockers, calcium channel blockers, digitalis, flecainide, moricizine, and propafenone. Non-cardiac drugs that may also cause bradycardia include atazanavir, lacosamide, lithium, mefloquine, succinylcholine, H2-receptor antagonists, tricyclic antidepressants, and anticholinesterase or cholinergic agents. Because bradycardia is a risk factor for torsade de pointes arrhythmia, concerns also exist when thalidomide is used with drugs that prolong the QT interval or cause torsade de pointes.', NULL, 'Caution is advised when thalidomide is used with drugs that can slow the heart rate or induce torsade de pointes arrhythmia.', NULL, 'Caution is advised when thalidomide is used with drugs that can slow the heart rate or induce torsade de pointes arrhythmia. Patients should be monitored for bradycardia, atrioventricular block and syncope, and advised to seek medical attention if they experience dizziness, lightheadedness, fainting, shortness of breath, or slow or irregular heartbeat. A dose reduction of thalidomide or discontinuation may be required.', 'Synergism', NULL, NULL, 'Moderate', NULL, 'DDInter', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/170759/', '[1] "Product Information. Thalomid (thalidomide)." Celgene Corporation, Warren, NJ.[2] Cerner Multum, Inc. "UK Summary of Product Characteristics." O 0[3] Cerner Multum, Inc. "Australian Product Information." O 0[4] Warrington SJ, Ankier SI, Turner P "Evaluation of possible interactions between ethanol and trazodone or amitriptyline." Neuropsychobiology 15 (1986): 31-7[5] "Product Information. Fycompa (perampanel)." Eisai Inc, Teaneck, NJ.[6] Gilman AG, Rall TW, Nies AS, Taylor P, eds. "Goodman and Gilman''s the Pharmacological Basis of Therapeutics. 8th ed." New York, NY: Pergamon Press Inc. (1990):[7] "Product Information. Rexulti (brexpiprazole)." Otsuka American Pharmaceuticals Inc, Rockville, MD.[8] Wohlt PD, Zheng L, Gunderson S, Balzar SA, Johnson BD, Fish JT "Recommendations for the use of medications with continuous enteral nutrition." Am J Health Syst Pharm 66 (2009): 1438-67[9] "Product Information. VFEND (voriconazole)." Pfizer U.S. Pharmaceuticals, New York, NY.', 1767365209);
