INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, arabic_effect, recommendation, arabic_recommendation, management_text, mechanism_text, alternatives_a, alternatives_b, risk_level, ddinter_id, source, type, metabolism_info, source_url, reference_text, updated_at) VALUES (170874, 'Thiotepa', 'St. John''s Wort', 'Moderate', 'Coadministration with St. John''s wort may alter the pharmacokinetics of antineoplastic agents that are substrates of the CYP450 3A4 isoenzyme (e.g., abiraterone, bexarotene, bortezomib, busulfan, cyclophosphamide, dasatinib, docetaxel, doxorubicin, etoposide, exemestane, ifosfamide, imatinib, irinotecan, letrozole, paclitaxel, sorafenib, sunitinib, tamoxifen, temsirolimus, teniposide, thiotepa, toremifene, tretinoin, vandetanib, vinca alkaloids). The mechanism is induction of CYP450 3A4 metabolism by constituents of St. John''s wort, which is expected to reduce the systemic levels and pharmacologic effects of many of these agents. In contrast, some agents such as cyclophosphamide and ifosfamide are prodrugs that are converted to active metabolites by CYP450 3A4, thus pharmacologic effects may be enhanced by St. John''s wort.', NULL, 'Until more information is available, the use of St.', NULL, 'Until more information is available, the use of St. John''s wort should probably be avoided before and during the entire course of chemotherapy. It is not known how much time should elapse between the discontinuation of St. John''s wort and initiation of chemotherapy. Patients should be advised to consult with their caregivers before using any herbal or alternative medicines.', 'Metabolism', NULL, NULL, 'Moderate', NULL, 'DDInter', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/171060/', '[1] Sparreboom A, Cox MC, Acharya MR, Figg WD "Herbal remedies in the United States: potential adverse interactions with anticancer agents." J Clin Oncol 22 (2004): 2489-503[2] Roby CA, Anderson GD, Kantor E, Dryer DA, Burstein AH "St John''s Wort: Effect on CYP3A4 activity." Clin Pharmacol Ther 67 (2000): 451-7[3] Patel S, Robinson R, Burk M "Hypertensive crisis associated with St. John''s Wort." Am J Med 112 (2002): 507-8', 1767365209);
INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, arabic_effect, recommendation, arabic_recommendation, management_text, mechanism_text, alternatives_a, alternatives_b, risk_level, ddinter_id, source, type, metabolism_info, source_url, reference_text, updated_at) VALUES (170875, 'Tofacitinib', 'Thiotepa', 'Major', 'Coadministration of tofacitinib with other immuno- or myelosuppressive agents may potentiate the risk of infections as well as lymphoma and other malignancies. Serious and sometimes fatal infections due to bacterial, mycobacterial, invasive fungal, viral, or other opportunistic pathogens have been reported in patients receiving tofacitinib, most of whom were taking concomitant immunosuppressants such as methotrexate or corticosteroids. Lymphoma and other malignancies have also been observed with tofacitinib use, with or without concomitant immunosuppressants.', NULL, 'Close monitoring for the development of infection is recommended if tofacitinib is used in combination with other immuno- or myelosuppressive agents, including the possible development of tuberculosis in patients who tested negative for latent tuberculosis infection prior to initiating therapy.', NULL, 'Close monitoring for the development of infection is recommended if tofacitinib is used in combination with other immuno- or myelosuppressive agents, including the possible development of tuberculosis in patients who tested negative for latent tuberculosis infection prior to initiating therapy. Lymphocyte and neutrophil counts as well as hemoglobin should be evaluated at baseline and regularly during therapy, and tofacitinib dosage adjusted as necessary in accordance with the product labeling. Patients should be advised to contact their physician if they develop signs and symptoms of infection such as fever, chills, diarrhea, sore throat, muscle aches, shortness of breath, blood in phlegm, weight loss, red or inflamed skin, body sores, and pain or burning during urination. If a serious infection, an opportunistic infection, or sepsis develops, tofacitinib should be interrupted until the infection is controlled.', 'Others', NULL, NULL, 'Major', NULL, 'DDInter', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/171061/', '[1] "Product Information. Xeljanz (tofacitinib)." Pfizer U.S. Pharmaceuticals Group, New York, NY.', 1767365209);
