INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, source, updated_at, management_text, mechanism_text, recommendation, arabic_recommendation, arabic_effect, risk_level, type) VALUES (1426007, 'Dexfenfluramine', 'Methylene blue', 'Major', 'Coadministration of methylene blue with serotonergic agents may potentiate the risk of serotonin syndrome, which is a rare but serious and potentially fatal condition thought to result from hyperstimulation of brainstem 5-HT1A and 2A receptors. Current research suggests that methylene blue has structural properties similar to monoamine oxidase inhibitors (MAOIs). As such, it may enhance serotonergic effects by inhibiting serotonin metabolism.', 'DDInter', 1767317476, 'Serotonergic agents should not be used in patients receiving methylene blue intravenously. Most serotonergic psychiatric drugs should be stopped 1 to 2 weeks (i.e., 4 to 5 half-lives) prior to treatment with methylene blue if possible, while others such as fluoxetine may require discontinuation up to 5 weeks in advance due to its prolonged half-life.', 'Synergism', 'Serotonergic agents should not be used in patients receiving methylene blue intravenously.', NULL, NULL, 'Major', 'pharmacodynamic');
INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, source, updated_at, management_text, mechanism_text, recommendation, arabic_recommendation, arabic_effect, risk_level, type) VALUES (1426008, 'Dexlansoprazole', 'Phenyl salicylate', 'Minor', 'Coadministration with proton pump inhibitors may decrease the oral bioavailability of aspirin and other salicylates. Proton pump inhibitors may enhance the release rate of salicylates from enteric-coated formulations due to premature disruption of the coating and intragastric release of the drug secondary to an increase in gastric pH. The clinical significance of this interaction is unknown. Theoretically, it may increase the risk of gastric adverse effects associated with salicylates.', 'DDInter', 1767317476, '-', 'Absorption', '-', NULL, NULL, 'Minor', 'pharmacodynamic');
