INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, source, updated_at, management_text, mechanism_text, recommendation, arabic_recommendation, arabic_effect, risk_level, type) VALUES (1426275, 'Imiglucerase', 'Miglustat', 'Moderate', 'Coadministration with miglustat may increase the clearance of imiglucerase. The mechanism of interaction has not been described. Imiglucerase had no significant effect on the pharmacokinetics of miglustat.', 'DDInter', 1767317476, 'Miglustat labeling states that use with imiglucerase is not indicated.', 'Excretion', 'Miglustat labeling states that use with imiglucerase is not indicated.', NULL, NULL, 'Moderate', 'pharmacodynamic');
INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, source, updated_at, management_text, mechanism_text, recommendation, arabic_recommendation, arabic_effect, risk_level, type) VALUES (1426276, 'Dobutamine', 'Milrinone', 'Minor', 'The positive inotropic effects of inamrinone and dobutamine appear to be additive. Milrinone should be expected to have the same interaction with dobutamine based on its structural and pharmacologic similarities to inamrinone.', 'DDInter', 1767317476, '-', 'Synergism', '-', NULL, NULL, 'Minor', 'pharmacodynamic');
INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, source, updated_at, management_text, mechanism_text, recommendation, arabic_recommendation, arabic_effect, risk_level, type) VALUES (1426277, 'Isocarboxazid', 'Milrinone', 'Moderate', 'Monoamine oxidase inhibitors (MAOIs) may potentiate the hypotensive effect of some medications. MAOIs alone quite commonly produce orthostatic hypotension. This effect may stem from a gradual MAOI-induced accumulation of false neurotransmitters in peripheral adrenergic neurons that have minimal activity at alpha- and beta-adrenergic receptors, resulting in a functional block of sympathetic neurotransmission. The interaction has been reported with the concomitant use of beta-blockers.', 'DDInter', 1767317476, 'Caution is advised during coadministration of MAOIs and other medications with hypotensive effects, especially during the first few weeks of treatment. Close monitoring for development of hypotension is recommended. Ambulatory patients should be advised to avoid rising abruptly from a sitting or recumbent position and to notify their physician if they experience dizziness, lightheadedness, syncope, orthostasis, or tachycardia.', 'Synergism', 'Caution is advised during coadministration of MAOIs and other medications with hypotensive effects, especially during the first few weeks of treatment.', NULL, NULL, 'Moderate', 'pharmacodynamic');
