    ) VALUES (1426303, 'Fenoldopam', 'Minoxidil', 'Moderate', 'The use of fenoldopam with other hypotensive agents has not been studied. However, these combinations would theoretically increase the risk of severe symptomatic hypotension.', NULL, 'Caution is strongly advised during concurrent use.', NULL, 'Caution is strongly advised during concurrent use. Inpatient monitoring of the heart rate and blood pressure is recommended. Sublingual nitroglycerin has been used safely with fenoldopam.', 'Synergism', NULL, NULL, 'Moderate', NULL, 'DDInter', 'pharmacodynamic', 1767317476);
