    ) VALUES (1426415, 'Sodium phosphate, monobasic', 'Lactulose', 'Moderate', 'In the treatment of portal-systemic encephalopathy, the loose stools produced by concomitant use of other laxatives may falsely suggest that adequate lactulose dosage has been achieved.', NULL, 'The use of other laxatives should generally be avoided when lactulose is prescribed for portal-systemic encephalopathy, especially during the initial phase of therapy.', NULL, 'The use of other laxatives should generally be avoided when lactulose is prescribed for portal-systemic encephalopathy, especially during the initial phase of therapy.', 'Others', NULL, NULL, 'Moderate', NULL, 'DDInter', 'pharmacodynamic', 1767317476);
INSERT OR IGNORE INTO drug_interactions (
        id, ingredient1, ingredient2, severity, effect, arabic_effect,
