    ) VALUES (1426422, 'Flucytosine', 'Moxetumomab Pasudotox', 'Moderate', 'Drugs that impair glomerular filtration may prolong the half-life of flucytosine and lead to accumulation.', NULL, 'Caution is advised if flucytosine is used in patients who have recently received or are receiving treatment with potentially nephrotoxic drugs (e.', NULL, 'Caution is advised if flucytosine is used in patients who have recently received or are receiving treatment with potentially nephrotoxic drugs (e.g., aminoglycosides; polypeptide, glycopeptide, and polymyxin antibiotics; amphotericin B; adefovir; cidofovir; tenofovir; foscarnet; cisplatin; deferasirox; gallium nitrate; lithium; mesalamine; certain immunosuppressants; intravenous bisphosphonates; intravenous pentamidine; high intravenous dosages of methotrexate; high dosages and/or chronic use of nonsteroidal anti-inflammatory agents).', 'Excretion', NULL, NULL, 'Moderate', NULL, 'DDInter', 'pharmacodynamic', 1767317476);
INSERT OR IGNORE INTO drug_interactions (
