        alternatives_a, alternatives_b, risk_level, ddinter_id, source, type, updated_at
    ) VALUES (1426434, 'Oxaliplatin', 'Moxetumomab Pasudotox', 'Moderate', 'Theoretically, coadministration with drugs that are nephrotoxic may delay and/or decrease the clearance of oxaliplatin, which is primarily eliminated unchanged by the kidney.', NULL, 'Caution is advised if oxaliplatin is used in patients who have recently received or are receiving treatment with potentially nephrotoxic drugs (e.', NULL, 'Caution is advised if oxaliplatin is used in patients who have recently received or are receiving treatment with potentially nephrotoxic drugs (e.g., aminoglycosides; polypeptide, glycopeptide, and polymyxin antibiotics; amphotericin B; adefovir; cidofovir; tenofovir; foscarnet; cisplatin; deferasirox; gallium nitrate; lithium; mesalamine; certain immunosuppressants; intravenous bisphosphonates; intravenous pentamidine; high intravenous dosages of methotrexate; high dosages and/or chronic use of nonsteroidal anti-inflammatory agents). The potential for increased toxicity of oxaliplatin such as peripheral sensory neuropathies and neutropenia should be considered. Renal function should be closely monitored during therapy.', 'Excretion', NULL, NULL, 'Moderate', NULL, 'DDInter', 'pharmacodynamic', 1767317476);
