INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, arabic_effect, recommendation, arabic_recommendation, management_text, mechanism_text, alternatives_a, alternatives_b, risk_level, ddinter_id, source, type, metabolism_info, source_url, reference_text, updated_at) VALUES (171317, 'Ticlopidine', 'Phenylbutazone', 'Moderate', 'Ticlopidine has been reported to potentiate the effect of nonsteroidal anti-inflammatory agents (NSAIDs) on platelet aggregation. The risk of bleeding may be increased, especially in patients with a history of gastrointestinal (GI) ulceration or bleeding.', NULL, 'Patients should be monitored for signs and symptoms of bleeding, especially GI bleeding, if NSAIDs and ticlopidine are coadministered.', NULL, 'Patients should be monitored for signs and symptoms of bleeding, especially GI bleeding, if NSAIDs and ticlopidine are coadministered. They should be advised to promptly report any signs of bleeding to their physician, including abdominal pain, vomiting blood, red or black stools, swelling, headache, dizziness, weakness, prolonged bleeding from cuts, nosebleeds, bleeding of gums from brushing, red or brown urine, or other unusual bleeding or bruising. Patients should also be counseled to avoid any other over-the-counter NSAID products.', 'Synergism', NULL, NULL, 'Moderate', NULL, 'DDInter', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/171503/', '[1] "Product Information. Ticlid (ticlopidine)." Syntex Laboratories Inc, Palo Alto, CA.[2] "Product Information. Ticlid (ticlopidine)." Syntex Laboratories Inc, Palo Alto, CA.', 1767363026);
INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, arabic_effect, recommendation, arabic_recommendation, management_text, mechanism_text, alternatives_a, alternatives_b, risk_level, ddinter_id, source, type, metabolism_info, source_url, reference_text, updated_at) VALUES (171318, 'Ticlopidine', 'Prednisolone', 'Minor', 'Limited data have shown that corticosteroids may significantly decrease ticlopidine-induced prolongations in bleeding times. The mechanism may be related to corticosteroid induced peripheral vasoconstriction as limited data have shown that corticosteroids do not affect ADP-mediated platelet aggregation in patients who are taking therapeutic doses of ticlopidine. The clinical relevance of this interaction is unknown, although it is suggested by some experts that systemic corticosteroids could be used prior to emergency surgery in patients who are taking ticlopidine. It is unknown whether the risk of thrombosis is increased in patients who are taking both drugs. More data are needed.', NULL, '-', NULL, '-', 'Others', NULL, NULL, 'Minor', NULL, 'DDInter', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/171504/', '[1] Thebault J, Blatrix C, Blanchard J, Panak E "A possible method to control prolongations of bleeding time under antiplatelet therapy with ticlopidine." Thromb Haemost 48 (1982): 6-8[2] "Product Information. Ticlid (ticlopidine)." Syntex Laboratories Inc, Palo Alto, CA.', 1767363026);
INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, arabic_effect, recommendation, arabic_recommendation, management_text, mechanism_text, alternatives_a, alternatives_b, risk_level, ddinter_id, source, type, metabolism_info, source_url, reference_text, updated_at) VALUES (171319, 'Ticlopidine', 'Rofecoxib', 'Moderate', 'Anticoagulants, antiplatelet agents, and thrombolytics may potentiate the risk of gastrointestinal bleeding complications associated with the use of nonsteroidal anti-inflammatory drugs (NSAIDs) including COX-2 inhibitors.', NULL, 'Although COX-2 inhibitors are often preferred over traditional, nonselective NSAIDs in patients who have increased risk for gastrointestinal toxicity, caution is advised during coadministration with anticoagulants or antiplatelet agents.', NULL, 'Although COX-2 inhibitors are often preferred over traditional, nonselective NSAIDs in patients who have increased risk for gastrointestinal toxicity, caution is advised during coadministration with anticoagulants or antiplatelet agents. Gastroprotective agents such as H2-receptor antagonists or proton pump inhibitors may be appropriate in high risk patients.', 'Synergism', NULL, NULL, 'Moderate', NULL, 'DDInter', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/171505/', '[1] "Product Information. Celebrex (celecoxib)." Searle, Chicago, IL.', 1767363026);
