INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, arabic_effect, recommendation, arabic_recommendation, management_text, mechanism_text, alternatives_a, alternatives_b, risk_level, ddinter_id, source, type, metabolism_info, source_url, reference_text, updated_at) VALUES (171321, 'Ticlopidine', 'Streptokinase', 'Moderate', 'Drugs that inhibit platelet function may increase the risk of bleeding when administered prior to, during, or after thrombolytic therapy.', NULL, 'Careful monitoring for signs of bleeding, particularly at arterial puncture wounds, is recommended when thrombolytics are used concurrently or sequentially with antiplatelet agents other than aspirin.', NULL, 'Careful monitoring for signs of bleeding, particularly at arterial puncture wounds, is recommended when thrombolytics are used concurrently or sequentially with antiplatelet agents other than aspirin. Some authorities recommend avoiding the initiation of platelet aggregation inhibitors within the first 24 hours following thrombolysis treatment.', 'Synergism', NULL, NULL, 'Moderate', NULL, 'DDInter', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/171507/', '[1] Harder S, Klinkhardt U "Thrombolytics: drug interactions of clinical significance." Drug Saf 23 (2000): 391-9[2] "Product Information. TNKase (tenecteplase)" Genentech, South San Francisco, CA.[3] "Product Information. Abbokinase (urokinase)." Abbott Pharmaceutical, Abbott Park, IL.[4] "Product Information. Retavase (reteplase)." Boehringer Mannheim, Gaithersburg, MD.[5] "Product Information. Ticlid (ticlopidine)." Syntex Laboratories Inc, Palo Alto, CA.[6] "Product Information. Activase (alteplase)." Genentech, South San Francisco, CA.[7] Hirsch J, Dalen J, Guyatt G, American College of Chest Physicians "The sixth (2000) ACCP guidelines for antithrombotic therapy for prevention and treatment of thrombosis. American College of Physicians." Chest 119(1 Suppl) (2001): 1S-2S[8] "Produ