INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, arabic_effect, recommendation, arabic_recommendation, management_text, mechanism_text, alternatives_a, alternatives_b, risk_level, ddinter_id, source, type, metabolism_info, source_url, reference_text, updated_at) VALUES (171418, 'Aluminum hydroxide', 'Timolol', 'Minor', 'Concurrent administration with aluminum and magnesium antacids has been shown to decrease the oral bioavailability of certain beta-blockers, although data are conflicting. The exact mechanism of interaction is unknown but may involve cation binding of beta-blockers or a reduction in the dissolution rate due to increased gastric pH. Based on available data, the clinical significance of this potential interaction is difficult to determine. As a precaution, patients may want to consider separating the administration times of beta-blockers and antacids or other aluminum- or magnesium-containing products by at least 2 hours.', NULL, '-', NULL, '-', 'Absorption', NULL, NULL, 'Minor', NULL, 'DDInter', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/171604/', '[1] D''Arcy PF, McElnay JC "Drug-antacid interactions: assessment of clinical importance." Drug Intell Clin Pharm 21 (1987): 607-17[2] Regardh CG, Lundborg P, Persson BA "The effect of antacid, metoclopramide, and propantheline on the bioavailability of metoprolol and atenolol." Biopharm Drug Dispos 2 (1981): 79-87[3] Hong CY, Hu SC, Lin SJ, Chiang BN "Lack of influence of aluminum hydroxide on the bioavailability and beta-adrenoceptor blocking activity of propranolol." Int J Clin Pharmacol Ther Toxicol 23 (1985): 244-6[4] Kirch W, Schafer-Korting M, Axthelm T, Kohler H, Mutschler E "Interaction of atenolol with furosemide and calcium and aluminum salts." Clin Pharmacol Ther 30 (1981): 429-35[5] Laer S, Neumann J, Scholz H "Interaction between sotalol and an antacid preparation." Br J Clin Pharmacol 43 (1997): 269-72[6] Gugler R, Allgayer H "Effects of antacids on the clinical pharmacokinetics of drugs. An update." Clin Pharmacokinet 18 (1990): 210-9[7] Dobbs JH, Skoutakis VA, Acchiardo SR, Dobbs BR "Effects of aluminum hydroxide on the absorption of propranolol." Curr Ther Res Clin Exp 21 (1977): 887-92', 1767363026);
INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, arabic_effect, recommendation, arabic_recommendation, management_text, mechanism_text, alternatives_a, alternatives_b, risk_level, ddinter_id, source, type, metabolism_info, source_url, reference_text, updated_at) VALUES (171419, 'Aminophylline', 'Timolol', 'Major', 'The pharmacologic effects of theophyllines and beta-blockers are opposite. Nonselective and high doses of cardioselective beta-blockers may cause severe or fatal bronchospasm by opposing theophylline-induced bronchodilation. Ophthalmic beta-blockers undergo significant systemic absorption and may also interact. In addition, propranolol and other beta-blockers may reduce the CYP450 hepatic metabolism of theophylline, and serum theophylline levels may be increased.', NULL, 'Oral and ophthalmic nonselective beta-blockers (e.', NULL, 'Oral and ophthalmic nonselective beta-blockers (e.g., carteolol, carvedilol, levobunolol, metipranolol, nadolol, oxprenolol, penbutolol, pindolol, propranolol, sotalol, and timolol) are considered contraindicated in patients with bronchospastic diseases. Cardioselective beta-blockers should generally be avoided, or used with extreme caution if no other alternatives are available and the benefits outweigh the risks of potentially severe bronchospasm. If patients do receive this combination, they should be closely monitored for increased serum theophylline levels but decreased bronchodilatory effectiveness.', 'Antagonism', NULL, NULL, 'Major', NULL, 'DDInter', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/171605/', '[1] Upton RA "Pharmacokinetic interactions between theophylline and other medication (Part II)." Clin Pharmacokinet 20 (1991): 135-50[2] Mue S, Sasaki T, Shibahara S, et alT "Influence of metoprolol on hemodynamics and respiratory function in asthmatic patients." Int J Clin Pharmacol Biopharm 17 (1979): 346-50[3] Lombardi TP, Bertino JS, Goldberg A, Middlet