INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, arabic_effect, recommendation, arabic_recommendation, management_text, mechanism_text, alternatives_a, alternatives_b, risk_level, ddinter_id, source, type, metabolism_info, source_url, reference_text, updated_at) VALUES (171458, 'Fenoldopam', 'Timolol', 'Moderate', 'The use of fenoldopam in combination with beta-blockers has not been studied. Theoretically, beta-blockers may potentiate the risk of severe, symptomatic hypotension by inhibiting the sympathetic reflex response to fenoldopam-induced blood pressure decreases.', NULL, 'Concomitant use of fenoldopam with beta-blockers should be avoided if possible.', NULL, 'Concomitant use of fenoldopam with beta-blockers should be avoided if possible.', 'Synergism', NULL, NULL, 'Moderate', NULL, 'DDInter', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/171645/', '[1] "Product Information. Corlopam (fenoldopam)." Neurex, Menlo Park, CA.', 1767363026);
INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, arabic_effect, recommendation, arabic_recommendation, management_text, mechanism_text, alternatives_a, alternatives_b, risk_level, ddinter_id, source, type, metabolism_info, source_url, reference_text, updated_at) VALUES (171459, 'Fenoprofen', 'Timolol', 'Moderate', 'Nonsteroidal anti-inflammatory drugs (NSAIDs) may attenuate the antihypertensive effect of beta-blockers. The proposed mechanism is NSAID-induced inhibition of renal prostaglandin synthesis, which results in unopposed pressor activity producing hypertension. In addition, NSAIDs can cause fluid retention, which also affects blood pressure. Indomethacin and piroxicam have been reported to have greater attenuating effects than other NSAIDs, and indomethacin effects may be significant in patients with eclampsia.', NULL, 'Patients receiving a beta-blocker who require prolonged (greater than 1 week) concomitant therapy with an NSAID should have blood pressure monitored more closely following initiation, discontinuation, or change of dosage of the NSAID.', NULL, 'Patients receiving a beta-blocker who require prolonged (greater than 1 week) concomitant therapy with an NSAID should have blood pressure monitored more closely following initiation, discontinuation, or change of dosage of the NSAID. The interaction is not expected to occur with low doses (e.g., low-dose aspirin) or intermittent short-term administration of NSAIDs.', 'Antagonism', NULL, NULL, 'Moderate', NULL, 'DDInter', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/171646/', '[1] Radack KL, Deck CC, Bloomfield SS "Ibuprofen interferes with the efficacy of antihypertensive drugs." Ann Intern Med 107 (1987): 628-35[2] Hartmann D, Stief G, Lingenfelder M, Guzelhan C, Horsch AK "Study on the possible interaction between tenoxicam and atenolol in hypertensive patients." Arzneimittelforschung 45-1 (1995): 494-8[3] Abate MA, Neely JL, Layne RD, D''Allessandri R "Interaction of indomethacin and sulindac with labetalol." Br J Clin Pharmacol 31 (1991): 363-6[4] Salvetti A, Pedrinelli R, Alberici P, Magagna A, Abdel-Haq B "The influence of indomethacin and sulindac on some pharmacological actions of atenolol in hypertensive patients." Br J Clin Pharmacol 17 Suppl 1 (1984): s108-11[5] Wong DG, Spence JD, Lamki L, Freeman D, McDonald JW "Effect of non-steroidal anti-inflammatory drugs on control of hypertension by beta-blockers and diuretics." Lancet 1 (1986): 997-1001[6] Ylitalo P, Pitkajarvi T, Pyykonen ML, Nurmi AK, Seppala E, Vapaatalo H "Inhibition of prostaglandin synthesis by indomethacin interacts with the antihypertensive effect of atenolol." Clin Pharmacol Ther 38 (1985): 443-9[7] Durao V, Prata MM, Concalves LM "Modification of antihypertensive effect of B-adrenoceptor-blocking agents by inhibition of endogenous prostaglandin synthesis." Lancet 2 (1977): 1005-7[8] Salvetti A, Arzilli F, Pedrinelli R, Beggi P, Motolese M "Interaction between oxprenolol and indomethacin on blood pressure in essential hypertensive patients." Eur J Clin Pharmacol 22 (1982): 197-201[9] Durao V, Prata MM, Goncalves LM "Modification of antihypertensive effect of beta-adrenoceptor-blocking agents by inhibition of endogenous prostaglandin synthesis." Lancet 2 (1977): 1005-7[10] Salvetti A, Pedrinelli R, Alberici P, Magagna A, Abdel-Haq B "The influence of indomethacin and sulindac on some pharmacological actions of atenolol in hypertensive patients." Br J Clin Pharmacol 17 Suppl 1 (1984): s108-11', 1767363026);
INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, arabic_effect, recommendation, arabic_recommendation, management_text, mechanism_text, alternatives_a, alternatives_b, risk_level, ddinter_id, source, type, metabolism_info, source_url, reference_text, updated_at) VALUES (171460, 'Fingolimod', 'Timolol', 'Major', 'The risk of severe bradycardia and atrioventricular (AV) block may be increased during initiation of fingolimod treatment in patients receiving other drugs that slow heart rate or AV conduction such as beta-blockers, certain calcium channel blockers (e.g., diltiazem, verapamil), and digitalis. Fingolimod can cause a decrease in heart rate during initiation of therapy that is apparent within an hour of the first dose and maximal at approximately 6 hours postdose in most cases, but occasionally up to 20 hours after the first dose.', NULL, 'Fingolimod has not been adequately studied in patients receiving beta-blockers, calcium channel blockers, or digitalis.', NULL, 'Fingolimod has not been adequately studied in patients receiving beta-blockers, calcium channel blockers, or digitalis. The possibility of switching to alternative agents that do not slow heart rate or AV conduction should be evaluated by the physician before initiating fingolimod. In patients who cannot switch, overnight continuous ECG monitoring after the first dose is recommended in accordance with the product labeling. The same precautions are applicable if, after the first month of treatment, fingolimod is discontinued for more than two weeks and then restarted, since the effects on heart rate and AV conduction may recur on reintroduction of fingolimod. Within the first 2 weeks of treatment, first-dose procedures are also recommended after interruption of one day or more; during week 3 and 4 of treatment, first-dose procedures are recommended after treatment interruption of more than 7 days. The first dose should always be administered in a setting where resources to appropriately manage symptomatic bradycardia are available.', 'Synergism', NULL, NULL, 'Major', NULL, 'DDInter', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/171647/', '[1] FDA. U.S. Food and Drug Administration "FDA Drug Safety Communication: Revised recommendations for cardiovascular monitoring and use of multiple sclerosis drug Gilenya (fingolimod). Available from: URL: http://www.fda.gov/Drugs/DrugSafety/ucm303192.htm#data." ([2012 May 14]):[2] "Product Information. Gilenya (fingolimod)." Novartis Pharmaceuticals, East Hanover, NJ.', 1767363026);
INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, arabic_effect, recommendation, arabic_recommendation, management_text, mechanism_text, alternatives_a, alternatives_b, risk_level, ddinter_id, source, type, metabolism_info, source_url, reference_text, updated_at) VALUES (171461, 'Flecainide', 'Timolol', 'Moderate', 'Beta-blockers and flecainide may have additive negative inotropic effects. Areas under the curve were increased for both drugs and negative inotropic effects occurred when flecainide and propranolol were given to normal subjects. A case of bradycardia, atrioventricular block and cardiac arrest has been reported after sotalol was added to flecainide.', NULL, 'Careful monitoring of the patient''s hemodynamic status is recommended during concomitant administration.', NULL, 'Careful monitoring of the patient''s hemodynamic status is recommended during concomitant administration. The same precaution should be observed when beta blocker ophthalmic solutions are used, since they are systemically absorbed and can produce clinically significant systemic effects even at low or undetectable plasma levels. Patients should be advised to promptly report symptoms such as dizziness, slow or irregular heartbeats, syncope, or palpitations.', 'Synergism', NULL, NULL, 'Moderate', NULL, 'DDInter', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/171648/', '[1] Lewis GP, Holtzman JL "Interaction of flecainide with digoxin and propranolol." Am J Cardiol 53 (1984): b52-7[2] Minish T, Herd A "Symptomatic bradycardia secondary to interaction between topical timolol maleate, verapamil, and flecainide: a case report." J Emerg Med 22 (2002): 247-9[3] Holtzman JL, Kvam DC, Berry DA, et al "The pharmacodynamic and pharmacokinetic interaction of flecainide acetate with propranolol: effects on cardiac function and drug clearance." Eur J Clin Pharmacol 33 (1987): 97-9[4] Myerburg RJ, Kessler KM, Cox MM, et al "Reversal of proarrhythmic effects of flecainide acetate and encainide hydrochloride by propranolol." Circulation 80 (1989): 1571-9[5] Warren R, Vohra J, Hunt D, Hamer A "Serious interactions of sotalol with amiodarone and flecainide." Med J Aust 152 (1990): 277', 1767363026);
INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, arabic_effect, recommendation, arabic_recommendation, management_text, mechanism_text, alternatives_a, alternatives_b, risk_level, ddinter_id, source, type, metabolism_info, source_url, reference_text, updated_at) VALUES (171462, 'Fludrocortisone', 'Timolol', 'Moderate', 'Corticosteroids may antagonize the effects of antihypertensive medications by inducing sodium and fluid retention. These effects may be more common with the natural corticosteroids (cortisone, hydrocortisone) because they have greater mineralocorticoid activity. Conversely, some calcium channel blockers such as diltiazem and verapamil may increase corticosteroid plasma levels and effects by inhibiting their clearance via CYP450 3A4 metabolism.', NULL, 'Patients on prolonged (i.', NULL, 'Patients on prolonged (i.e., longer than about a week) or high-dose corticosteroid therapy should have blood pressure, electrolyte levels, and body weight monitored regularly, and be observed for the development of edema and congestive heart failure. The dosages of antihypertensive medications may require adjustment.', 'Metabolism, Antagonism', NULL, NULL, 'Moderate', NULL, 'DDInter', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/171649/', '[1] "Multum Information Services, Inc. Expert Review Panel"[2] Cerner Multum, Inc. "UK Summary of Product Characteristics." O 0', 1767363026);
