INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, arabic_effect, recommendation, arabic_recommendation, management_text, mechanism_text, alternatives_a, alternatives_b, risk_level, ddinter_id, source, type, metabolism_info, source_url, reference_text, updated_at) VALUES (171552, 'Timolol', 'Riociguat', 'Moderate', 'Riociguat may potentiate the action of antihypertensive agents and other vasodilators. Symptomatic hypotension may occur. Riociguat stimulates soluble guanylate cyclase (sGC), an enzyme in the cardiopulmonary system that binds with nitric oxide (NO) to catalyze the synthesis of cyclic guanosine monophosphate (cGMP). The resultant accumulation of cGMP in vascular smooth muscle cells leads to peripheral vasodilation and blood pressure decreases.', NULL, 'Caution is advised if riociguat is prescribed in combination with antihypertensive agents or other vasodilators.', NULL, 'Caution is advised if riociguat is prescribed in combination with antihypertensive agents or other vasodilators.', 'Synergism', 'Latanoprost, Apraclonidine, Methazolamide, Neostigmine, Pilocarpine, Physostigmine, Acetylcholine, Diclofenamide, Bimatoprost, Travoprost, Acetazolamide, More', 'Bosentan, Macitentan, Metyrosine', 'Moderate', NULL, 'DDInter', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/171740/', '[1] "Product Information. Adempas (riociguat)." Bayer Pharmaceutical Inc, West Haven, CT.[2] "Product Information. Adempas (riociguat)." Bayer Pharmaceutical Inc, West Haven, CT.', 1767369485);
INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, arabic_effect, recommendation, arabic_recommendation, management_text, mechanism_text, alternatives_a, alternatives_b, risk_level, ddinter_id, source, type, metabolism_info, source_url, reference_text, updated_at) VALUES (171553, 'Timolol', 'Ritodrine', 'Major', 'The use of a nonselective beta blocker with ritodrine (a beta-2 agonist) may result in mutually antagonistic effects, negating the effect of one or both drugs.', NULL, 'In general, the use of beta blockers with ritodrine is discouraged.', NULL, 'In general, the use of beta blockers with ritodrine is discouraged. Selective beta blockers (e.g. acebutolol, atenolol, betaxolol, bisoprolol, metoprolol) may be preferable if the use of a beta blocker is unavoidable.', 'Antagonism', 'Latanoprost, Apraclonidine, Methazolamide, Neostigmine, Pilocarpine, Clonidine, Physostigmine, Acetylcholine, Diclofenamide, Bimatoprost, Travoprost, More', 'Bremelanotide, Black cohosh, Flibanserin', 'Major', NULL, 'DDInter', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/171741/', '[1] "Product Information. Yutopar (ritodrine)." Astra USA, Westborough, MA.', 1767369485);
