INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, arabic_effect, recommendation, arabic_recommendation, management_text, mechanism_text, alternatives_a, alternatives_b, risk_level, ddinter_id, source, type, metabolism_info, source_url, reference_text, updated_at) VALUES (171688, 'Fedratinib', 'Tipranavir', 'Major', 'Theoretically, tipranavir may potentiate the risk of bleeding in patients treated with agents that affect hemostasis such as anticoagulants, platelet inhibitors, thrombin inhibitors, thrombolytic agents, high supplemental dosages of vitamin E, or agents that commonly cause thrombocytopenia.', NULL, 'Caution is advised if tipranavir/ritonavir is used in combination with other drugs that affect hemostasis.', NULL, 'Caution is advised if tipranavir/ritonavir is used in combination with other drugs that affect hemostasis. Patients should be advised to promptly report any signs of bleeding to their physician, including pain, swelling, headache, dizziness, weakness, prolonged bleeding from cuts, increased menstrual flow, vaginal bleeding, nosebleeds, bleeding of gums from brushing, unusual bleeding or bruising, red or brown urine, or red or black stools.', 'Synergism', 'Lamivudine, Paritaprevir, Glecaprevir, Tenofovir alafenamide, Cabotegravir, Valaciclovir, Zanamivir, Velpatasvir, Oseltamivir, Acyclovir, Brincidofovir, More', 'Entrectinib, Imatinib, Dacomitinib, Bosutinib, Lapatinib, Selumetinib, Ribociclib, Vandetanib, Cobimetinib, Duvelisib, Tivozanib, More', 'Major', NULL, 'DDInter', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/171876/', '[1] "Product Information. Aptivus (tipranavir)." Boehringer-Ingelheim, Ridgefield, CT.[2] "Product Information. Brukinsa (zanubrutinib)." BeiGene USA, Inc, San Mateo, CA.[3] Canadian Pharmacists Association "e-CPS. Available from: URL: http://www.pharmacists.ca/function/Subscriptions/ecps.cfm?link=eCPS_quikLink."[4] "Product Information. Aptivus (tipranavir)."