INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, arabic_effect, recommendation, arabic_recommendation, management_text, mechanism_text, alternatives_a, alternatives_b, risk_level, ddinter_id, source, type, metabolism_info, source_url, reference_text, updated_at) VALUES (171699, 'Glasdegib', 'Tipranavir', 'Moderate', 'Coadministration with glasdegib may increase the plasma concentrations of drugs that are substrates of the P-glycoprotein (P-gp) and/or breast cancer resistance protein (BCRP) efflux transporters.', NULL, 'Caution is advised if glasdegib is coadministered with drugs that are substrates of P-gp and/or BCRP, particularly those with a narrow therapeutic range.', NULL, 'Caution is advised if glasdegib is coadministered with drugs that are substrates of P-gp and/or BCRP, particularly those with a narrow therapeutic range. Clinical and laboratory monitoring should be considered whenever glasdegib is added to or withdrawn from therapy with these drugs, and dosages adjusted as necessary. Patients should be monitored for the development of adverse effects.', 'Absorption', 'Lamivudine, Glecaprevir, Simeprevir, Cabotegravir, Zanamivir, Valaciclovir, Velpatasvir, Dolutegravir, Oseltamivir, Acyclovir, Brincidofovir, More', 'Pertuzumab, Olaparib, Oxaliplatin, Rucaparib, Bortezomib, Tisagenlecleucel, Omacetaxine mepesuccinate, Altretamine, Tagraxofusp, Ixazomib, Enasidenib, More', 'Moderate', NULL, 'DDInter', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/171887/', '[1] "Product Information. Daurismo (glasdegib)." Pfizer U.S. Pharmaceuticals Group, New York, NY.[2] Cerner Multum, Inc. "UK Summary of Product Characteristics." O 0[3] "Product Information. Daurismo (glasdegib)." Pfizer U.S. Pharmaceuticals Group, New York, NY.', 1767369485);
