INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, arabic_effect, recommendation, arabic_recommendation, management_text, mechanism_text, alternatives_a, alternatives_b, risk_level, ddinter_id, source, type, metabolism_info, source_url, reference_text, updated_at) VALUES (171727, 'Tipranavir', 'Quinestrol', 'Moderate', 'Use of tipranavir in combination with estrogens may be associated with an increased risk of skin rash. The mechanism has not been described. Mild to moderate rashes including urticarial rash, maculopapular rash, and possible photosensitivity have been reported in 14% of females and 8% to 10% of males receiving tipranavir in combination with low-dose ritonavir in phase 2 and 3 trials.', NULL, 'Women who use estrogens should be advised of a potentially increased risk of skin rash during concomitant treatment with tipranavir.', NULL, 'Women who use estrogens should be advised of a potentially increased risk of skin rash during concomitant treatment with tipranavir. If a rash occurs, it is usually mild to moderate. Nevertheless, patients should be instructed to contact their physician promptly, as a temporary discontinuation of tipranavir or the estrogen may be necessary.', 'Synergism', 'Lamivudine, Glecaprevir, Tenofovir alafenamide, Maraviroc, Amprenavir, Simeprevir, Valaciclovir, Zanamivir, Cabotegravir, Velpatasvir, Rilpivirine, More', NULL, 'Moderate', NULL, 'DDInter', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/171915/', '[1] "Product Information. Aptivus (tipranavir)." Boehringer-Ingelheim, Ridgefield, CT.[2] Canadian Pharmacists Association "e-CPS. Available from: URL: http://www.pharmacists.ca/function/Subscriptions/ecps.cfm?link=eCPS_quikLink."[3] "Product Information. Aptivus (tipranavir)." Boehringer-Ingelheim, Ridgefield, CT.[4] Cerner Multum, Inc. "Australian Product Information." O 0[5] Cerner Multum, Inc. "UK Summary of Product Characteristics." O 0[6] Schubert W, Eriksson U, 