    ) VALUES (1426634, 'Quinidine', 'Nadolol', 'Moderate', 'Quinidine and beta-blockers may have additive negative inotropic effects, which can be beneficial when used to treat atrial fibrillation. However, adverse effects have been reported with quinidine and some beta blockers.', NULL, 'Patients should be monitored for orthostatic hypotension, cardiac arrhythmias, bradycardia, and heart failure, and beta-blocker dosage should be decreased if necessary.', NULL, 'Patients should be monitored for orthostatic hypotension, cardiac arrhythmias, bradycardia, and heart failure, and beta-blocker dosage should be decreased if necessary.', 'Synergism', NULL, NULL, 'Moderate', NULL, 'DDInter', 'pharmacodynamic', 1767317476);
