    ) VALUES (1426644, 'Sufentanil', 'Nadolol', 'Moderate', 'In patients receiving chronic beta-blocker therapy, the use of sufentanil may lead to a higher degree of bradycardia and hypotension. One study has shown that induction and maintenance of anesthesia requires lower doses of sufentanil in patients receiving beta-blockers.', NULL, 'Patients should be carefully observed for clinically significant alterations in hemodynamics.', NULL, 'Patients should be carefully observed for clinically significant alterations in hemodynamics.', 'Synergism', NULL, NULL, 'Moderate', NULL, 'DDInter', 'pharmacodynamic', 1767317476);
