        alternatives_a, alternatives_b, risk_level, ddinter_id, source, type, updated_at
    ) VALUES (1426705, 'Iron', 'Esomeprazole', 'Moderate', 'The profound hypochlorhydria induced by proton pump inhibitors (PPIs) may impair the gastrointestinal absorption of nonheme iron, a process that is dependent on an acidic environment.', NULL, 'Patients with iron deficiency may not respond adequately to oral iron replacement therapy during coadministration of proton pump inhibitors.', NULL, 'Patients with iron deficiency may not respond adequately to oral iron replacement therapy during coadministration of proton pump inhibitors. If an interaction is suspected after ruling out other causes, it may be appropriate to discontinue the proton pump inhibitor or consider administering iron parenterally.', 'Absorption', NULL, NULL, 'Moderate', NULL, 'DDInter', 'pharmacodynamic', 1767317476);
