        alternatives_a, alternatives_b, risk_level, ddinter_id, source, type, updated_at
    ) VALUES (1426796, 'Esomeprazole', 'Mycophenolate mofetil', 'Moderate', 'Coadministration with proton pump inhibitors may reduce the bioavailability of mycophenolic acid (MPA) from the administration of mycophenolate mofetil. The exact mechanism of interaction has not been established, but may involve a decrease in MPA solubility at higher gastric pH levels.', NULL, 'Because clinical relevance of this interaction has not been established, proton pump inhibitors should be used with caution in transplant patients receiving mycophenolate mofetil.', NULL, 'Because clinical relevance of this interaction has not been established, proton pump inhibitors should be used with caution in transplant patients receiving mycophenolate mofetil.', 'Absorption', NULL, NULL, 'Moderate', NULL, 'DDInter', 'pharmacodynamic', 1767317476);
