    ) VALUES (1426800, 'Esomeprazole', 'Nelfinavir', 'Major', 'Concurrent use of proton pump inhibitors may decrease the oral bioavailability of nelfinavir and reduce its concentrations in plasma. Nelfinavir solubility may decrease with increasing pH, thus inhibition of gastric acid secretion may interfere with dissolution of the drug.', NULL, 'Given the risk of reduced viral susceptibility and resistance development associated with subtherapeutic antiretroviral drug levels, use of proton pump inhibitors is not recommended in patients treated with nelfinavir.', NULL, 'Given the risk of reduced viral susceptibility and resistance development associated with subtherapeutic antiretroviral drug levels, use of proton pump inhibitors is not recommended in patients treated with nelfinavir.', 'Absorption', NULL, NULL, 'Major', NULL, 'DDInter', 'pharmacodynamic', 1767317476);
