        alternatives_a, alternatives_b, risk_level, ddinter_id, source, type, updated_at
    ) VALUES (1426846, 'Ranitidine (bismuth citrate)', 'Naproxen', 'Moderate', 'The concomitant administration of H2 blockers may increase the dissolution rate of enteric-coated naproxen, causing the drug to be released in the stomach instead of the small intestine.', NULL, 'H2 antagonists should be avoided in patients taking enteric-coated formulations of naproxen.', NULL, 'H2 antagonists should be avoided in patients taking enteric-coated formulations of naproxen.', 'Absorption', NULL, NULL, 'Moderate', NULL, 'DDInter', 'pharmacodynamic', 1767317476);
