        id, ingredient1, ingredient2, severity, effect, arabic_effect,
        recommendation, arabic_recommendation, management_text, mechanism_text,
        alternatives_a, alternatives_b, risk_level, ddinter_id, source, type, updated_at
    ) VALUES (1426854, 'Rucaparib', 'Naproxen', 'Moderate', 'Coadministration with rucaparib may increase the plasma concentrations and the risk of adverse effects of drugs that are substrates of CYP450 1A2. The proposed mechanism is decreased clearance due to rucaparib-mediated inhibition of CYP450 1A2.', NULL, 'Caution is advised if rucaparib is used concomitantly with drugs that are substrates of CYP450 1A2, particularly those with a narrow therapeutic range.', NULL, 'Caution is advised if rucaparib is used concomitantly with drugs that are substrates of CYP450 1A2, particularly those with a narrow therapeutic range. Dosage adjustments as well as clinical and laboratory monitoring should be considered whenever rucaparib is added to or withdrawn from therapy with these drugs. Patients should be monitored for the development of adverse effects.', 'Metabolism', NULL, NULL, 'Moderate', NULL, 'DDInter', 'pharmacodynamic', 1767317476);
INSERT OR IGNORE INTO drug_interactions (
        id, ingredient1, ingredient2, severity, effect, arabic_effect,
