        alternatives_a, alternatives_b, risk_level, ddinter_id, source, type, updated_at
    ) VALUES (1426855, 'Esomeprazole', 'Selpercatinib', 'Major', 'Coadministration with drugs that increase gastric pH may significantly decrease the oral bioavailability of selpercatinib and reduce its concentrations in plasma.', NULL, 'Concomitant use of selpercatinib with proton pump inhibitors should generally be avoided.', NULL, 'Concomitant use of selpercatinib with proton pump inhibitors should generally be avoided. If coadministration is required, the manufacturer recommends taking selpercatinib with food to minimize the clinical impact of the interaction.', 'Absorption', NULL, NULL, 'Major', NULL, 'DDInter', 'pharmacodynamic', 1767317476);
