INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, arabic_effect, recommendation, arabic_recommendation, management_text, mechanism_text, alternatives_a, alternatives_b, risk_level, ddinter_id, source, type, metabolism_info, source_url, reference_text, updated_at) VALUES (172048, 'Tobramycin', 'Mezlocillin', 'Moderate', 'INTERVAL: Mezlocillin may inactivate aminoglycosides in vivo and in vitro. The mechanism is complexation with the aminoglycoside. Typically, this effect is significant only in patients with renal failure or if the agents are mixed in the same IV container or line. Lower serum aminoglycoside levels may be seen, and dosage adjustments may be necessary.', NULL, 'Mezlocillin and aminoglycosides should be administered separately during combined therapy.', NULL, 'Mezlocillin and aminoglycosides should be administered separately during combined therapy.', 'Others', NULL, NULL, 'Moderate', NULL, 'DDInter', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/172236/', '[1] Pickering LK, Rutherford I "Effect of concentration and time upon inactivation of tobramycin, gentamicin, netilmicin and amikacin by azlocillin, carbenicillin, mecillinam, mezlocillin and piperacillin." J Pharmacol Exp Ther 217 (1981): 345-9[2] Russo ME, Atkin-Thor E "Gentamicin and ticarcillin in subjects with end-stage renal disease." Clin Nephrol 15 (1981): 175-80[3] Viollier AF, Standiford HC, Drusano GL, Tatem BA, Moody MR, Schimpff SC "Comparative pharmacokinetics and serum bactericidal activity of mezlocillin, ticarcillin and piperacillin, with and without gentamicin." J Antimicrob Chemother 15 (1985): 597-606[4] "Product Information. Arikayce (amikacin liposome)." Insmed Incorporated, Bridgewater, NJ.[5] Henderson JL, Polk RE, Kline BJ "In vitro inactivation of gentamicin, tobramycin, and netilmicin by carbenicillin, azlocillin, or mezlocillin." Am J Hosp Pharm 38 (1981): 1167-70[6] Russo ME "Penicillin-aminoglycoside inactivat