    ) VALUES (1427152, 'Axitinib', 'Netupitant', 'Moderate', 'Coadministration with inhibitors of CYP450 3A4 may increase the plasma concentrations of axitinib, which is primarily metabolized by the isoenzyme.', NULL, 'Caution is advised if axitinib is prescribed with CYP450 3A4 inhibitors.', NULL, 'Caution is advised if axitinib is prescribed with CYP450 3A4 inhibitors. Alternative agents with no or minimal CYP450 3A4 inhibition potential are recommended whenever possible. Otherwise, patients should be monitored closely for development of toxicity such as hypertension/hypertensive crisis, arterial and venous thromboembolic complications, hemorrhage, gastrointestinal perforation or fistula, thyroid dysfunction, reversible posterior leukoencephalopathy syndrome, proteinuria, and liver enzyme elevations or hepatic impairment.', 'Metabolism', NULL, NULL, 'Moderate', NULL, 'DDInter', 'pharmacodynamic', 1767317476);
INSERT OR IGNORE INTO drug_interactions (
