        alternatives_a, alternatives_b, risk_level, ddinter_id, source, type, updated_at
    ) VALUES (1427168, 'Cannabidiol', 'Netupitant', 'Minor', 'Cannabidiol is a substrate for CYP450 3A4 and CYP450 2C19. Cannabidiol is metabolized in the liver and the gut (primarily in the liver) by CYP450 2C19 and CYP450 3A4 enzymes, and UGT1A7, UGT1A9, and UGT2B7 isoforms.', NULL, '-', NULL, '-', 'Metabolism', NULL, NULL, 'Minor', NULL, 'DDInter', 'pharmacodynamic', 1767317476);
