    ) VALUES (1427170, 'Carbamazepine', 'Netupitant', 'Major', 'Coadministration with potent inducers of CYP450 3A4 may significantly decrease the plasma concentrations of netupitant.', NULL, 'Concomitant use of netupitant or its prodrug, fosnetupitant, should generally be avoided during chronic administration of potent CYP450 3A4 inducers due to the potential for reduced efficacy of netupitant.', NULL, 'Concomitant use of netupitant or its prodrug, fosnetupitant, should generally be avoided during chronic administration of potent CYP450 3A4 inducers due to the potential for reduced efficacy of netupitant.', 'Metabolism', NULL, NULL, 'Major', NULL, 'DDInter', 'pharmacodynamic', 1767317476);
