INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, arabic_effect, recommendation, arabic_recommendation, management_text, mechanism_text, alternatives_a, alternatives_b, risk_level, ddinter_id, source, type, metabolism_info, source_url, reference_text, updated_at) VALUES (172507, 'Idelalisib', 'Tolcapone', 'Moderate', 'Coadministration of idelalisib with other agents known to induce hepatotoxicity may potentiate the risk of liver injury. The use of idelalisib has been associated with elevations in alanine aminotransferase (ALT) and aspartate aminotransferase (AST) greater than 5 times the upper limit of normal.', NULL, 'The use of idelalisib with other potentially hepatotoxic agents should be avoided whenever possible (e.', NULL, 'The use of idelalisib with other potentially hepatotoxic agents should be avoided whenever possible (e.g., acetaminophen; alcohol; amiodarone; androgens and anabolic steroids; antituberculous agents; azole antifungal agents; ACE inhibitors; cyclosporine (high dosages); disulfiram; endothelin receptor antagonists; interferons; ketolide and macrolide antibiotics; kinase inhibitors; methotrexate; nonsteroidal anti-inflammatory agents; nucleoside reverse transcriptase inhibitors; proteasome inhibitors; retinoids; sulfonamides; tamoxifen; tetracyclines; thiazolidinediones; tolvaptan; vincristine; zileuton; anticonvulsants such as carbamazepine, hydantoins, felbamate, and valproic acid; lipid-lowering medications such as fenofibrate, lomitapide, mipomersen, niacin, and statins; herbals and nutritional supplements such as black cohosh, chaparral, comfrey, DHEA, kava, pennyroyal oil, and red yeast rice). Patients treated with idelalisib should have serum ALT, AST, and bilirubin measured prior to initiation of treatment and regularly during treatment in accordance with the product labeling, and the dosing adjusted or interrupted as necessary.', 'Synergism', NULL, NULL, 'Moderate', NULL, 'DDInter', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/172695/', '[1] "Product Information. Zydelig (idelalisib)." Gilead Sciences, Foster City, CA.[2] "Product Information. Hivid (zalcitabine)." Roche Laboratories, Nutley, NJ.', 1767363026);
INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, arabic_effect, recommendation, arabic_recommendation, management_text, mechanism_text, alternatives_a, alternatives_b, risk_level, ddinter_id, source, type, metabolism_info, source_url, reference_text, updated_at) VALUES (172508, 'Iloperidone', 'Tolcapone', 'Moderate', 'Agents with central antidopaminergic activity such as phenothiazines, neuroleptics, and certain antiemetics may antagonize the pharmacologic effects of dopaminergic drugs, and vice versa. In addition, the central nervous system depressant and hypotensive effects of these agents may be additively or synergistically increased when taken together.', NULL, 'Concomitant use of dopaminergic drugs with antidopaminergic agents should generally be avoided.', NULL, 'Concomitant use of dopaminergic drugs with antidopaminergic agents should generally be avoided. If coadministration is necessary, patients should be alerted to the possibility of excessive drowsiness and monitored for potentially diminished therapeutic response to both treatments. Patients treated for Parkinson''s disease should generally avoid antidopaminergic agents, particularly phenothiazines and older neuroleptic agents, since these agents may cause extrapyramidal reactions and exacerbate the symptoms of Parkinson''s disease. Likewise, patients with a major psychotic disorder should ordinarily not be treated with dopaminergic drugs because of the risk of exacerbating the psychosis with an increase in central dopaminergic tone.', 'Synergism', NULL, NULL, 'Moderate', NULL, 'DDInter', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/172696/', '[1] Weingarten JC, Thompson TL "The effect of thioridazine on prolactinoma growth in a schizophrenic man: case report." Gen Hosp Psychiatry 7 (1985): 364-6[2] "Product Information. Norprolac (quinagolide)." Ferring Inc, North York, IA.[3] "Product Information. Requip (ropinirole)." SmithKline Beecham